import { auth } from "./firebase";

// Direct external API URL - call the backend service directly
const API_BASE_URL = import.meta.env.VITE_API_BASE_URL || 'https://cp-backend-service-test-972540571952.asia-south1.run.app';

export interface UserProfile {
  uid: string;
  email: string;
  firstName: string;
  lastName: string;
  username?: string;
  hasBasicInfo: boolean;
  verified?: boolean;
  phoneNumber?: string;
  title?: string;
  positionDesignation?: string;
  currentCompany?: string;
  about?: string;
  gender?: string;
  dateOfBirth?: string;
  city?: string;
  state?:
    | string
    | {
        code: string;
        name: string;
      };
  country?:
    | string
    | {
        code: string;
        name: string;
      };
  photoUrl?: string;
  bannerUrl?: string;
  userType?: string;
  organizationName?: string;
  isActive?: boolean;
  createdTime?: string;
