export interface UpdateUserData {
  userType?: "personal" | "business";
  phoneNumber?: string;
  title?: string;
  positionDesignation?: string;
  gender?: string;
  dateOfBirth?: string;
  city?: string;
  state?: { name: string; code: string };
  country?: { name: string; code: string };
  company?: string;
