  bannerPic?: File | string;
}

// Types for Education, Experience, and Projects
export interface Education {
  id?: string;
  degree: string;
  fieldOfStudy: string;
  schoolOrCollege: string;
  startDate: string;
  endDate: string;
  grade: string;
}

export interface Experience {
  id?: string;
  companyName: string;
  title: string;
  employmentType: string;
  location: string;
  startDate: string;
  endDate: string;
  isCurrent: boolean;
  description: string;
}

