                      title: '',
                      customTitle: '',
                      positionDesignation: '',
                      company: '',
                      gender: '',
                      phoneNumber: '',
                      countryCode: '+91',
                      country: { name: '', code: '' },
                      state: { name: '', code: '' },
                      city: '',
                      hidePhoneNumber: false,
                    });
                    setBusinessData({
                      companyName: '',
                      industry: '',
                      customIndustry: '',
                      companyType: '',
                      description: '',
                      addressLine1: '',
                      addressLine2: '',
                      city: '',
                      state: { name: '', code: '' },
                      country: { name: '', code: '' },
                      pincode: '',
                      website: '',
                      registrationNumber: '',
                      companySize: '',
                      phoneNumber: '',
