import { useState, useEffect } from "react";
import { Card, CardContent } from "../components/ui/card";
import { Button } from "../components/ui/button";
import { Avatar, AvatarFallback, AvatarImage } from "../components/ui/avatar";
import { Separator } from "../components/ui/separator";
import { DropdownMenu, DropdownMenuContent, DropdownMenuTrigger, DropdownMenuItem } from "../components/ui/dropdown-menu";
import { 
  ThumbsUp, 
  MessageCircle, 
  Share2, 
  Bookmark, 
  MoreHorizontal, 
  Calendar,
  Plus,
  Eye,
  Flag
} from "lucide-react";
import Header from "../components/layout/header";
import SidebarLeft from "../components/layout/sidebar-left";
import SidebarRight from "../components/layout/sidebar-right";
import MobileNavigation from "../components/mobile-navigation";
import { useAuth } from "../contexts/AuthContext";
import { useLocation } from "wouter";
import { useIsMobile } from "../hooks/use-mobile";
import NewsPostModal from "../components/NewsPostModal";
import CommentSection from "../components/CommentSection";

interface Post {
  id: string;
  uid: string;
  headline: string;
  content: string;
  imageUrl?: string;
  imageUrls?: string[];
  likeCount: number;
  commentCount: number;
  createdTime: string;
  authorName?: string;
  authorTitle?: string;
  authorProfilePic?: string;
}

// Mock posts data
const mockPosts: Post[] = [
  {
    id: "1",
    uid: "user1",
    headline: "Revolutionary Construction Technology Transforms Building Process",
    content: "A groundbreaking new construction method has been unveiled that promises to reduce building time by 40% while maintaining structural integrity. This innovative approach combines advanced materials with AI-powered planning systems.\n\nThe technology has already been successfully implemented in three major projects across different regions, showing consistent results and positive feedback from both contractors and clients.",
    imageUrls: [
      "https://images.unsplash.com/photo-1541888946425-d81bb19240f5?w=800&h=400&fit=crop",
      "https://images.unsplash.com/photo-1504307651254-35680f356dfd?w=800&h=400&fit=crop"
    ],
    likeCount: 45,
    commentCount: 12,
    createdTime: "2024-01-15T10:30:00Z",
    authorName: "Sarah Chen",
    authorTitle: "Senior Construction Engineer",
    authorProfilePic: "https://images.unsplash.com/photo-1494790108755-2616b612b8c5?w=150&h=150&fit=crop&crop=face"
  },
  {
    id: "2", 
    uid: "user2",
    headline: "Sustainable Building Materials Gain Momentum in Industry",
    content: "The construction industry is experiencing a shift towards eco-friendly materials. Recent studies show that sustainable options not only reduce environmental impact but also provide long-term cost benefits for developers and property owners.",
    imageUrl: "https://images.unsplash.com/photo-1558618666-fcd25c85cd64?w=800&h=400&fit=crop",
    likeCount: 32,
    commentCount: 8,
    createdTime: "2024-01-14T14:45:00Z",
    authorName: "Michael Rodriguez",
    authorTitle: "Sustainability Consultant",
    authorProfilePic: "https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=150&h=150&fit=crop&crop=face"
  },
  {
    id: "3",
    uid: "user3", 
    headline: "Safety Protocols Updated for High-Rise Construction",
    content: "New safety guidelines have been implemented following comprehensive industry research. These updates address emerging challenges in modern construction while ensuring worker protection remains the top priority.",
    likeCount: 28,
    commentCount: 15,
    createdTime: "2024-01-13T09:15:00Z",
    authorName: "Jennifer Park",
    authorTitle: "Safety Coordinator",
    authorProfilePic: "https://images.unsplash.com/photo-1438761681033-6461ffad8d80?w=150&h=150&fit=crop&crop=face"
  }
];

export default function HomePage() {
  const [posts, setPosts] = useState<Post[]>([]);
  const [isPostModalOpen, setIsPostModalOpen] = useState(false);
  const [likedPosts, setLikedPosts] = useState<Set<string>>(new Set());
  const [expandedComments, setExpandedComments] = useState<Set<string>>(new Set());
  const { user } = useAuth();
  const [, navigate] = useLocation();
  const isMobile = useIsMobile();

  useEffect(() => {
    // Simulate API call - replace with actual API integration
    setPosts(mockPosts);
  }, []);

  const formatTimeAgo = (dateString: string) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffInHours = Math.floor((now.getTime() - date.getTime()) / (1000 * 60 * 60));

    if (diffInHours < 1) return "Just now";
    if (diffInHours < 24) return `${diffInHours}h`;
    if (diffInHours < 168) return `${Math.floor(diffInHours / 24)}d`;
    return date.toLocaleDateString();
  };

  const handleLikePost = (postId: string) => {
    const newLikedPosts = new Set(likedPosts);
    if (likedPosts.has(postId)) {
      newLikedPosts.delete(postId);
    } else {
      newLikedPosts.add(postId);
    }
    setLikedPosts(newLikedPosts);

    // Update post like count
    setPosts(prevPosts => 
      prevPosts.map(post => 
        post.id === postId 
          ? { 
              ...post, 
              likeCount: likedPosts.has(postId) 
                ? post.likeCount - 1 
                : post.likeCount + 1 
            }
          : post
      )
    );
  };

  const handlePostClick = (postId: string) => {
    navigate(`/posts/${postId}`);
  };

  const handleToggleComments = (postId: string) => {
    const newExpandedComments = new Set(expandedComments);
    if (expandedComments.has(postId)) {
      newExpandedComments.delete(postId);
    } else {
      newExpandedComments.add(postId);
    }
    setExpandedComments(newExpandedComments);
  };

  const handlePostSubmit = async (postData: { headline: string; content: string; images: File[] }) => {
    // This would typically make an API call to create the post
    console.log("Creating post:", postData);

    // For now, add to local state (in real app, refetch from API)
    const newPost: Post = {
      id: Date.now().toString(),
      uid: user?.uid || "current-user",
      headline: postData.headline,
      content: postData.content,
      imageUrls: postData.images.length > 0 ? ["https://images.unsplash.com/photo-1486406146926-c627a92ad1ab?w=800&h=400&fit=crop"] : undefined,
      likeCount: 0,
      commentCount: 0,
      createdTime: new Date().toISOString(),
      authorName: user?.displayName || "Current User",
      authorTitle: "Professional",
      authorProfilePic: user?.photoURL || undefined
    };

    setPosts(prevPosts => [newPost, ...prevPosts]);
    setIsPostModalOpen(false);
  };

  return (
    <div className="min-h-screen bg-cmo-background">
      <Header />
      <div className="max-w-7xl mx-auto px-4 py-6">
        <div className="grid grid-cols-1 lg:grid-cols-12 gap-6">
          {/* Left Sidebar */}
          <div className="lg:col-span-3">
            <SidebarLeft />
          </div>

          {/* Main Content */}
          <div className="lg:col-span-6">
            <div className="space-y-4">
              {/* Create Post Section */}
              <Card className="bg-cmo-card border-cmo-border">
                <CardContent className="p-4">
                  <div className="flex items-center space-x-3">
                    <Avatar className="w-10 h-10">
                      <AvatarImage src={user?.photoURL || undefined} />
                      <AvatarFallback className="bg-cmo-primary text-white">
                        {user?.displayName?.charAt(0) || user?.email?.charAt(0) || "U"}
                      </AvatarFallback>
                    </Avatar>
                    <Button
                      variant="outline"
                      className="flex-1 justify-start text-cmo-text-secondary border-cmo-border hover:bg-gray-50"
                      onClick={() => setIsPostModalOpen(true)}
                      data-testid="button-create-post"
                    >
                      <Plus className="w-4 h-4 mr-2" />
                      Share a post...
                    </Button>
                  </div>
                </CardContent>
              </Card>

              {/* Posts Feed */}
              {posts.map((post) => (
                <Card key={post.id} className="bg-cmo-card border-cmo-border">
                  <CardContent className="p-4">
                    <div className="space-y-3">
                      {/* Post Header */}
                      <div className="flex items-start justify-between">
                        <div className="flex items-center space-x-3">
                          <Avatar className="w-10 h-10">
                            <AvatarImage src={post.authorProfilePic} />
                            <AvatarFallback className="bg-cmo-primary text-white">
                              {post.authorName?.split(' ').map(n => n[0]).join('') || 
                               post.uid?.charAt(0)?.toUpperCase() || "P"}
                            </AvatarFallback>
                          </Avatar>
                          <div>
                            <h3 className="font-medium text-sm text-cmo-text-primary">
                              {post.authorName || "Anonymous User"}
                            </h3>
                            {post.authorTitle && (
                              <p className="text-xs text-cmo-text-secondary">
                                {post.authorTitle}
                              </p>
                            )}
                            <div className="flex items-center text-xs text-cmo-text-secondary mt-1">
                              <Calendar className="w-3 h-3 mr-1" />
                              {formatTimeAgo(post.createdTime)}
                            </div>
                          </div>
                        </div>

                        <DropdownMenu>
                          <DropdownMenuTrigger asChild>
                            <Button variant="ghost" size="sm" className="h-8 w-8 p-0">
                              <MoreHorizontal className="h-4 w-4" />
                            </Button>
                          </DropdownMenuTrigger>
                          <DropdownMenuContent align="end">
                            <DropdownMenuItem>
                              <Eye className="w-4 h-4 mr-2" />
                              View Details
                            </DropdownMenuItem>
                            <DropdownMenuItem>
                              <Flag className="w-4 h-4 mr-2" />
                              Report Post
                            </DropdownMenuItem>
                          </DropdownMenuContent>
                        </DropdownMenu>
                      </div>

                      {/* Post Content - Clickable */}
                      <div 
                        className="mb-3 cursor-pointer hover:bg-gray-50 p-2 rounded transition-colors"
                        onClick={() => handlePostClick(post.id)}
                        data-testid={`post-content-${post.id}`}
                      >
                        <h3 className="font-semibold text-sm text-cmo-text-primary mb-2">
                          {post.headline}
                        </h3>
                        <p className="text-sm text-cmo-text-secondary leading-relaxed">
                          {post.content.length > 200 ? `${post.content.substring(0, 200)}...` : post.content}
                        </p>
                      </div>

                      {/* Post Images */}
                      {(post.imageUrls || post.imageUrl) && (
                        <div className="mb-3">
                          {post.imageUrls && post.imageUrls.length > 1 ? (
                            <div className="grid grid-cols-2 gap-1 rounded-lg overflow-hidden">
                              {post.imageUrls.map((imageUrl, index) => (
                                <img
                                  key={index}
                                  src={imageUrl}
                                  alt={`${post.headline} - Image ${index + 1}`}
                                  className={`w-full object-cover ${
                                    post.imageUrls!.length === 2 ? 'h-48' : 'h-32'
                                  }`}
                                  onError={(e) => {
                                    e.currentTarget.style.display = "none";
                                  }}
                                />
                              ))}
                            </div>
                          ) : (
                            <img
                              src={post.imageUrls?.[0] || post.imageUrl}
                              alt={post.headline}
                              className="w-full h-64 object-cover rounded-lg"
                              onError={(e) => {
                                e.currentTarget.style.display = "none";
                              }}
                            />
                          )}
                        </div>
                      )}

                      {/* Engagement Stats */}
                      <div className="flex items-center justify-between text-xs text-cmo-text-secondary mb-3">
                        <div className="flex items-center space-x-4">
                          {post.likeCount > 0 && (
                            <div className="flex items-center">
                              <div className="w-4 h-4 bg-blue-500 rounded-full flex items-center justify-center mr-2">
                                <ThumbsUp className="w-2 h-2 text-white fill-current" />
                              </div>
                              <span>{post.likeCount}</span>
                            </div>
                          )}
                        </div>
                        {post.commentCount > 0 && (
                          <span>
                            {post.commentCount} comment{post.commentCount > 1 ? "s" : ""}
                          </span>
                        )}
                      </div>

                      <Separator className="mb-3" />

                      {/* Action Buttons */}
                      <div className="flex items-center justify-between">
                        <Button
                          variant="ghost"
                          size="sm"
                          className={`flex-1 hover:bg-gray-50 py-2 ${
                            likedPosts.has(post.id) ? 'text-blue-600' : 'text-cmo-text-secondary'
                          }`}
                          onClick={() => handleLikePost(post.id)}
                          data-testid={`button-like-${post.id}`}
                        >
                          <ThumbsUp className="w-4 h-4 mr-1" />
                          <span className="text-xs">Like</span>
                        </Button>
                        <CommentSection
                          postId={post.id}
                          commentCount={post.commentCount}
                          isExpanded={expandedComments.has(post.id)}
                          onToggleExpand={() => handleToggleComments(post.id)}
                        />
                        <Button
                          variant="ghost"
                          size="sm"
                          className="flex-1 hover:bg-gray-50 text-cmo-text-secondary py-2"
                          data-testid={`button-share-${post.id}`}
                        >
                          <Share2 className="w-4 h-4 mr-1" />
                          <span className="text-xs">Share</span>
                        </Button>
                        <Button
                          variant="ghost"
                          size="sm"
                          className="flex-1 hover:bg-gray-50 text-cmo-text-secondary py-2"
                          data-testid={`button-save-${post.id}`}
                        >
                          <Bookmark className="w-4 h-4 mr-1" />
                          <span className="text-xs">Save</span>
                        </Button>
                      </div>

                      {/* Comments Section */}
                      {expandedComments.has(post.id) && (
                        <div className="mt-4">
                          <CommentSection
                            postId={post.id}
                            commentCount={post.commentCount}
                            isExpanded={true}
                            onToggleExpand={() => handleToggleComments(post.id)}
                          />
                        </div>
                      )}
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          </div>

          {/* Right Sidebar */}
          <div className="lg:col-span-3">
            <SidebarRight />
          </div>
        </div>
      </div>

      {/* Post Modal */}
      <NewsPostModal
        isOpen={isPostModalOpen}
        onClose={() => setIsPostModalOpen(false)}
        onSubmit={handlePostSubmit}
        title="Create Post"
        submitButtonText="Post"
      />

      {/* Mobile Navigation */}
      {isMobile && <MobileNavigation />}
    </div>
  );
}

client/src/pages/login.tsx.content:
import React, { useState } from 'react';
import { Link, useLocation } from 'wouter';
import { Button } from '../components/ui/button';
import { Input } from '../components/ui/input';
import { Label } from '../components/ui/label';
import { Checkbox } from '../components/ui/checkbox';
import { Card, CardContent } from '../components/ui/card';
import { Eye, EyeOff, Loader2 } from 'lucide-react';
import { signInWithGoogle, signInWithEmail } from '../lib/firebase';
import { useToast } from '../hooks/use-toast';

export function LoginPage() {
  const [showPassword, setShowPassword] = useState(false);
  const [rememberMe, setRememberMe] = useState(false);
  const [loading, setLoading] = useState(false);
  const [googleLoading, setGoogleLoading] = useState(false);
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);

    try {
      await signInWithEmail(formData.email, formData.password);

      // Handle remember me functionality
      if (rememberMe) {
        localStorage.setItem('rememberMe', 'true');
      } else {
        localStorage.removeItem('rememberMe');
      }

      toast({
        title: "Welcome back!",
        description: "Successfully signed in to your account.",
      });

      // Redirect to home page
      setLocation("/");
    } catch (error: any) {
      console.error("Login error:", error);
      toast({
        title: "Login failed",
        description: error.message || "Invalid email or password. Please try again.",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const handleGoogleSignin = async () => {
    setGoogleLoading(true);

    try {
      await signInWithGoogle();

      toast({
        title: "Welcome!",
        description: "Successfully signed in with Google.",
      });

      // Redirect to home page
      setLocation("/");
    } catch (error: any) {
      console.error("Google signin error:", error);
      toast({
        title: "Google signin failed",
        description: error.message || "Something went wrong. Please try again.",
        variant: "destructive",
      });
    } finally {
      setGoogleLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex flex-col lg:flex-row">
      {/* Left Side - Hero Section */}
      <div className="flex-1 bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-100 relative overflow-hidden lg:flex hidden">
        <div className="absolute inset-0 bg-gradient-to-br from-cmo-primary/10 to-purple-600/20"></div>

        {/* Geometric Background Pattern */}
        <div className="absolute inset-0 opacity-20">
          <svg className="w-full h-full" viewBox="0 0 400 400" fill="none">
            <defs>
              <pattern id="hexagons" x="0" y="0" width="50" height="43.4" patternUnits="userSpaceOnUse">
                <path d="M25 0L50 14.4V35.6L25 50L0 35.6V14.4L25 0Z" stroke="currentColor" strokeWidth="1" fill="none"/>
              </pattern>
            </defs>
            <rect width="100%" height="100%" fill="url(#hexagons)" className="text-cmo-primary/30"/>
          </svg>
        </div>

        <div className="relative z-10 flex items-center justify-center w-full p-8">
          <div className="text-center max-w-md">
            <h1 className="text-lg font-bold text-gray-800 mb-4">
              Professional network for construction industry
            </h1>
            <p className="text-sm sm:text-base text-gray-600 leading-relaxed">
              Connect with construction professionals, engineers, and project managers.
              Build your professional network in the construction sector.
            </p>
          </div>
        </div>

        {/* Navigation Dots */}
        <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 flex space-x-2">
          <div className="w-2 h-2 bg-cmo-primary rounded-full"></div>
          <div className="w-2 h-2 bg-gray-300 rounded-full"></div>
          <div className="w-2 h-2 bg-gray-300 rounded-full"></div>
        </div>
      </div>

      {/* Right Side - Login Form */}
      <div className="flex-1 flex items-center justify-center p-3 sm:p-6 bg-white min-h-screen lg:min-h-auto relative">
        <div className="w-full max-w-sm">
          {/* Mobile Header - Only visible on mobile */}
          <div className="lg:hidden text-center mb-6">
            <h1 className="text-sm font-bold text-gray-800 mb-2">
              Welcome to CP
            </h1>
            <p className="text-sm text-gray-600">
              Professional network for construction industry
            </p>
          </div>

          {/* Logo */}
          <div className="flex items-center mb-6">
            <div className="w-8 h-8 bg-cmo-primary rounded-lg flex items-center justify-center mr-3">
              <div className="w-4 h-4 bg-white rounded-sm"></div>
            </div>
            <span className="text-sm font-bold text-cmo-text-primary">CP</span>
          </div>

          {/* Form Header */}
          <div className="mb-6">
            <h2 className="text-lg font-bold text-cmo-text-primary mb-2">Sign in</h2>
            <p className="text-cmo-text-secondary">Welcome back! Please enter your details.</p>
          </div>

          {/* Login Form */}
          <form onSubmit={handleSubmit} className="space-y-4">
            {/* Email Field */}
            <div className="space-y-2">
              <Label htmlFor="email">Email</Label>
              <Input
                id="email"
                name="email"
                type="email"
                placeholder="Enter your email"
                className="h-11"
                value={formData.email}
                onChange={handleInputChange}
                required
              />
            </div>

            {/* Password Field */}
            <div className="space-y-2">
              <Label htmlFor="password">Password</Label>
              <div className="relative">
                <Input
                  id="password"
                  name="password"
                  type={showPassword ? "text" : "password"}
                  placeholder="Enter your password"
                  className="h-11 pr-10"
                  value={formData.password}
                  onChange={handleInputChange}
                  required
                />
                <button
                  type="button"
                  onClick={() => setShowPassword(!showPassword)}
                  className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600"
                >
                  {showPassword ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
                </button>
              </div>
            </div>

            {/* Remember Me & Forgot Password */}
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-2">
                <Checkbox
                  id="remember"
                  checked={rememberMe}
                  onCheckedChange={(checked) => setRememberMe(checked === true)}
                />
                <Label htmlFor="remember" className="text-sm">Remember for 30 days</Label>
              </div>
              <Link href="/forgot-password">
                <Button variant="link" className="p-0 h-auto text-sm text-red-500 hover:text-red-600">
                  Forgot password
                </Button>
              </Link>
            </div>

            {/* Sign In Button */}
            <Button 
              type="submit" 
              className="w-full h-11 bg-cmo-primary hover:bg-cmo-primary/90"
              disabled={loading}
            >
              {loading ? (
                <>
                  <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                  Signing in...
                </>
              ) : (
                "Sign in"
              )}
            </Button>

            {/* Social Login */}
            <div className="space-y-3">
              <div className="relative">
                <div className="absolute inset-0 flex items-center">
                  <span className="w-full border-t border-gray-200" />
                </div>
                <div className="relative flex justify-center text-xs uppercase">
                  <span className="bg-white px-2 text-gray-500">OR SIGN IN WITH</span>
                </div>
              </div>

              <div className="grid grid-cols-2 gap-3">
                <Button
                  type="button"
                  variant="outline"
                  className="h-10 text-gray-700 border-gray-200 hover:bg-gray-50 justify-center"
                  onClick={handleGoogleSignin}
                  disabled={googleLoading}
                >
                  {googleLoading ? (
                    <Loader2 className="w-5 h-5 mr-2 animate-spin" />
                  ) : (
                    <svg className="w-5 h-5 mr-2" viewBox="0 0 24 24">
                      <path fill="#4285F4" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"/>
                      <path fill="#34A853" d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"/>
                      <path fill="#FBBC05" d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"/>
                      <path fill="#EA4335" d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"/>
                    </svg>
                  )}
                  Google
                </Button>

                <Button
                  type="button"
                  variant="outline"
                  className="h-10 text-gray-700 border-gray-200 hover:bg-gray-50 justify-center"
                >
                  <svg className="w-5 h-5 mr-2" fill="#1877F2" viewBox="0 0 24 24">
                    <path d="M24 12.073c0-6.627-5.373-12-12-12s-12 5.373-12 12c0 5.99 4.388 10.954 10.125 11.854v-8.385H7.078v-3.47h3.047V9.43c0-3.007 1.792-4.669 4.533-4.669 1.312 0 2.686.235 2.686.235v2.953H15.83c-1.491 0-1.956.925-1.956 1.874v2.25h3.328l-.532 3.47h-2.796v8.385C19.612 23.027 24 18.062 24 12.073z"/>
                  </svg>
                  Facebook
                </Button>
              </div>
            </div>

            {/* Sign Up Link */}
            <div className="text-center">
              <span className="text-sm text-gray-500">Create an account? </span>
              <Link href="/signup">
                <Button variant="link" className="p-0 h-auto text-sm font-semibold">
                  SignUp
                </Button>
              </Link>
            </div>
          </form>
        </div>

        {/* Footer - Outside form container, at bottom of page */}
        <div className="absolute bottom-6 left-4 right-4">
          <div className="flex justify-between items-center text-xs text-gray-500">
            <span>© 2024 CP Inc. All rights reserved.</span>
            <div className="flex space-x-4">
              <Link href="/privacy-policy" className="hover:text-gray-700">
                Privacy Policy
              </Link>
              <Link href="/terms-conditions" className="hover:text-gray-700">
                Terms & Conditions
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

