import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'wouter';
import { Button } from '../components/ui/button';
import { Input } from '../components/ui/input';
import { Label } from '../components/ui/label';
import { Eye, EyeOff, Lock, Loader2 } from 'lucide-react';
import { resetPassword, verifyResetCode } from '../lib/firebase';
import { useToast } from '../hooks/use-toast';

export function ResetPasswordPage() {
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [verifying, setVerifying] = useState(true);
  const [resetCode, setResetCode] = useState('');
  const [email, setEmail] = useState('');
  const [, setLocation] = useLocation();
  const { toast } = useToast();

  const [formData, setFormData] = useState({
    password: '',
    confirmPassword: '',
  });

  // Extract reset code from URL parameters
  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const oobCode = urlParams.get('oobCode');

    if (oobCode) {
      setResetCode(oobCode);
      // Verify the reset code
      verifyCode(oobCode);
    } else {
      toast({
        title: "Invalid reset link",
        description: "This password reset link is invalid or expired.",
        variant: "destructive",
      });
      setLocation("/forgot-password");
    }
  }, []);

  const verifyCode = async (code: string) => {
    try {
      const userEmail = await verifyResetCode(code);
      setEmail(userEmail);
      setVerifying(false);
    } catch (error: any) {
      console.error("Reset code verification error:", error);
      toast({
        title: "Invalid reset link",
        description: "This password reset link is invalid or expired.",
        variant: "destructive",
      });
      setLocation("/forgot-password");
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (formData.password !== formData.confirmPassword) {
      toast({
        title: "Password mismatch",
        description: "Passwords do not match. Please try again.",
        variant: "destructive",
      });
      return;
    }

    if (formData.password.length < 8) {
      toast({
        title: "Password too short",
        description: "Password must be at least 8 characters long.",
        variant: "destructive",
      });
      return;
    }

    setLoading(true);

    try {
      await resetPassword(resetCode, formData.password);

      toast({
        title: "Password updated!",
        description: "Your password has been successfully updated.",
      });

      // Navigate to success page
      setLocation("/password-reset-success");
    } catch (error: any) {
      console.error("Password reset error:", error);
      toast({
        title: "Password reset failed",
        description: error.message || "Something went wrong. Please try again.",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  if (verifying) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="text-center">
          <Loader2 className="w-8 h-8 animate-spin mx-auto mb-4" />
          <p className="text-gray-600">Verifying reset link...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex flex-col lg:flex-row">
      {/* Left Side - Hero Section */}
      <div className="flex-1 bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-100 relative overflow-hidden lg:flex hidden">
        <div className="absolute inset-0 bg-gradient-to-br from-cmo-primary/10 to-purple-600/20"></div>

        {/* Geometric Background Pattern */}
        <div className="absolute inset-0 opacity-20">
          <svg className="w-full h-full" viewBox="0 0 400 400" fill="none">
            <defs>
              <pattern id="hexagons" x="0" y="0" width="50" height="43.4" patternUnits="userSpaceOnUse">
                <path d="M25 0L50 14.4V35.6L25 50L0 35.6V14.4L25 0Z" stroke="currentColor" strokeWidth="1" fill="none"/>
              </pattern>
            </defs>
            <rect width="100%" height="100%" fill="url(#hexagons)" className="text-cmo-primary/30"/>
          </svg>
        </div>

        <div className="relative z-10 flex items-center justify-center w-full p-8">
          <div className="text-center max-w-md">
            <h1 className="text-2xl sm:text-3xl font-bold text-gray-800 mb-4">
              Professional network for construction industry
            </h1>
            <p className="text-sm sm:text-base text-gray-600 leading-relaxed">
              Connect with construction professionals, engineers, and project managers.
              Build your professional network in the construction sector.
            </p>
          </div>
        </div>

        {/* Navigation Dots */}
        <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 flex space-x-2">
          <div className="w-2 h-2 bg-cmo-primary rounded-full"></div>
          <div className="w-2 h-2 bg-gray-300 rounded-full"></div>
          <div className="w-2 h-2 bg-gray-300 rounded-full"></div>
        </div>
      </div>

      {/* Right Side - Reset Password Form */}
      <div className="flex-1 flex items-center justify-center p-4 sm:p-8 bg-white min-h-screen lg:min-h-auto">
        <div className="w-full max-w-sm">
          {/* Logo */}
          <div className="flex items-center mb-8">
            <div className="w-8 h-8 bg-cmo-primary rounded-lg flex items-center justify-center mr-3">
              <div className="w-4 h-4 bg-white rounded-sm"></div>
            </div>
            <span className="text-lg font-bold text-cmo-text-primary">CP</span>
          </div>

          {/* Lock Icon */}
          <div className="w-12 h-12 bg-gray-100 rounded-full flex items-center justify-center mb-6">
            <Lock className="w-6 h-6 text-gray-600" />
          </div>

          {/* Form Header */}
          <div className="mb-8">
            <h2 className="text-lg font-bold text-cmo-text-primary mb-2">Set new password</h2>
            <p className="text-cmo-text-secondary">Your new password must be different from previous used passwords.</p>
          </div>

          {/* Reset Form */}
          <form onSubmit={handleSubmit} className="space-y-6">
            {/* Email Display */}
            <div className="text-sm text-gray-600 mb-4">
              Resetting password for: <strong>{email}</strong>
            </div>

            {/* New Password Field */}
            <div className="space-y-2">
              <Label htmlFor="password">New Password</Label>
              <div className="relative">
                <Input
                  id="password"
                  name="password"
                  type={showPassword ? "text" : "password"}
                  placeholder="Enter your new password"
                  className="h-11 pr-10 border-gray-300 focus:border-gray-500"
                  value={formData.password}
                  onChange={handleInputChange}
                  disabled={loading}
                  required
                />
                <button
                  type="button"
                  onClick={() => setShowPassword(!showPassword)}
                  className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600"
                  disabled={loading}
                >
                  {showPassword ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
                </button>
              </div>
            </div>

            {/* Confirm Password Field */}
            <div className="space-y-2">
              <Label htmlFor="confirmPassword">Confirm Password</Label>
              <div className="relative">
                <Input
                  id="confirmPassword"
                  name="confirmPassword"
                  type={showConfirmPassword ? "text" : "password"}
                  placeholder="Confirm your new password"
                  className="h-11 pr-10 border-gray-300 focus:border-gray-500"
                  value={formData.confirmPassword}
                  onChange={handleInputChange}
                  disabled={loading}
                  required
                />
                <button
                  type="button"
                  onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                  className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600"
                  disabled={loading}
                >
                  {showConfirmPassword ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
                </button>
              </div>
            </div>

            {/* Password Requirements */}
            <div className="text-sm text-gray-600 space-y-1">
              <p>Must be at least 8 characters.</p>
              <p>Must contain one special character.</p>
            </div>

            {/* Update Button */}
            <Button 
              type="submit" 
              className="w-full h-11 bg-cmo-primary hover:bg-cmo-primary/90"
              disabled={loading}
            >
              {loading ? (
                <>
                  <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                  Updating password...
                </>
              ) : (
                'Update password'
              )}
            </Button>
          </form>

          {/* Footer */}
          <div className="mt-8 text-center">
            <p className="text-sm text-gray-500 mb-4">© 2024 CP Inc. All rights reserved.</p>
            <div className="flex justify-center space-x-6 text-sm text-gray-500">
              <Link href="/privacy-policy">
                <Button variant="link" className="p-0 h-auto text-sm text-gray-500 hover:text-gray-700">
                  Privacy Policy
                </Button>
              </Link>
              <Link href="/terms-conditions">
                <Button variant="link" className="p-0 h-auto text-sm text-gray-500 hover:text-gray-700">
                  Terms & Conditions
                </Button>
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/security-dashboard-simp.content:
import React, { useEffect, useState } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '../components/ui/card';
import { Badge } from '../components/ui/badge';
import { Button } from '../components/ui/button';
import { Progress } from '../components/ui/progress';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '../components/ui/tabs';
import { Alert, AlertDescription, AlertTitle } from '../components/ui/alert';
import { 
  Shield, 
  TrendingUp, 
  TrendingDown, 
  AlertTriangle, 
  CheckCircle2, 
  Clock, 
  DollarSign,
  Users,
  Lock,
  Database,
  Wifi,
  BookOpen,
  ArrowLeft
} from 'lucide-react';
import { Link } from 'wouter';

interface SecurityAssessment {
  id: string;
  companyName: string;
  industry: string;
  companySize: string;
  overallScore: number;
  riskLevel: string;
  currentSecurityTools: string[];
  complianceRequirements: string[];
  assessmentData: any;
  recommendations?: SecurityRecommendation[];
  createdAt: string;
  updatedAt: string;
}

interface SecurityRecommendation {
  id: string;
  category: string;
  title: string;
  description: string;
  priority: string;
  difficulty: string;
  estimatedCost: string;
  estimatedTimeToImplement: string;
  benefits: string[];
  steps: any[];
  resources: any;
  isImplemented: boolean;
  implementationNotes?: string;
  implementedAt?: string;
}

interface SecurityMetric {
  id: string;
  metricType: string;
  value: number;
  previousValue?: number;
  changePercentage?: number;
  recordedAt: string;
}

const getCategoryIcon = (category: string) => {
  switch (category) {
    case 'access_control': return <Lock className="w-4 h-4" />;
    case 'data_protection': return <Database className="w-4 h-4" />;
    case 'network_security': return <Wifi className="w-4 h-4" />;
    case 'employee_training': return <BookOpen className="w-4 h-4" />;
    default: return <Shield className="w-4 h-4" />;
  }
};

const getPriorityColor = (priority: string) => {
  switch (priority) {
    case 'critical': return 'destructive';
    case 'high': return 'destructive';
    case 'medium': return 'default';
    case 'low': return 'secondary';
    default: return 'default';
  }
};

const getRiskColor = (risk: string) => {
  switch (risk) {
    case 'critical': return 'text-red-600';
    case 'high': return 'text-orange-600';
    case 'medium': return 'text-yellow-600';
    case 'low': return 'text-green-600';
    default: return 'text-gray-600';
  }
};

export function SecurityDashboard() {
  const [assessment, setAssessment] = useState<SecurityAssessment | null>(null);
  const [metrics, setMetrics] = useState<SecurityMetric[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch data from API
    const fetchData = async () => {
      try {
        // Fetch assessments
        const assessmentsResponse = await fetch('/api/security/assessments');
        const assessments = await assessmentsResponse.json();

        if (assessments.length > 0) {
          // Fetch detailed assessment with recommendations
          const detailResponse = await fetch(`/api/security/assessments/${assessments[0].id}`);
          const detailedAssessment = await detailResponse.json();
          setAssessment(detailedAssessment);
        }

        // Fetch metrics
        const metricsResponse = await fetch('/api/security/metrics');
        const metricsData = await metricsResponse.json();
        setMetrics(metricsData);

        setLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleImplementRecommendation = async (recommendationId: string) => {
    try {
      const response = await fetch(`/api/security/recommendations/${recommendationId}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ 
          isImplemented: true, 
          implementationNotes: 'Marked as implemented' 
        }),
      });

      if (response.ok) {
        const updatedRec = await response.json();
        // Update the local state
        if (assessment) {
          const updatedRecommendations = assessment.recommendations?.map(rec => 
            rec.id === recommendationId ? updatedRec : rec
          );
          setAssessment({
            ...assessment,
            recommendations: updatedRecommendations
          });
        }
      }
    } catch (error) {
      console.error('Error updating recommendation:', error);
    }
  };

  if (loading) {
    return (
      <div className="p-8">
        <div className="animate-pulse space-y-4">
          <div className="h-8 bg-gray-200 rounded w-1/4"></div>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            {[1, 2, 3, 4].map(i => (
              <div key={i} className="h-32 bg-gray-200 rounded"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  const recommendations = assessment?.recommendations || [];
  const criticalRecs = recommendations.filter(r => r.priority === 'critical');
  const implementedRecs = recommendations.filter(r => r.isImplemented);

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <Link href="/">
                <Button variant="ghost" size="sm">
                  <ArrowLeft className="w-4 h-4 mr-2" />
                  Back to Home
                </Button>
              </Link>
              <div>
                <h1 className="text-2xl font-bold text-gray-900">Security Dashboard</h1>
                <p className="text-gray-600">AI-powered security recommendations for {assessment?.companyName}</p>
              </div>
            </div>
            <Badge variant="outline" className="px-3 py-1">
              <Shield className="w-4 h-4 mr-2" />
              {assessment?.industry}
            </Badge>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6 space-y-6">
        {/* Security Score Overview */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Security Score</CardTitle>
              <Shield className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{assessment?.overallScore}/100</div>
              <div className="flex items-center space-x-2 mt-2">
                <Progress value={assessment?.overallScore} className="flex-1" />
                <span className={`text-sm font-medium ${getRiskColor(assessment?.riskLevel || '')}`}>
                  {assessment?.riskLevel ? assessment.riskLevel.toUpperCase() : 'UNKNOWN'}
                </span>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Critical Issues</CardTitle>
              <AlertTriangle className="h-4 w-4 text-red-500" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-red-600">{criticalRecs.length}</div>
              <p className="text-xs text-muted-foreground">
                Require immediate attention
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Implemented</CardTitle>
              <CheckCircle2 className="h-4 w-4 text-green-500" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-green-600">{implementedRecs.length}</div>
              <p className="text-xs text-muted-foreground">
                Out of {recommendations.length} recommendations
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Progress</CardTitle>
              <TrendingUp className="h-4 w-4 text-blue-500" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">
                {recommendations.length > 0 ? Math.round((implementedRecs.length / recommendations.length) * 100) : 0}%
              </div>
              <p className="text-xs text-muted-foreground">
                Completion rate
              </p>
            </CardContent>
          </Card>
        </div>

        {/* Main Content Tabs */}
        <Tabs defaultValue="recommendations" className="space-y-4">
          <TabsList>
            <TabsTrigger value="recommendations">Recommendations</TabsTrigger>
            <TabsTrigger value="metrics">Security Metrics</TabsTrigger>
            <TabsTrigger value="assessment">Assessment Details</TabsTrigger>
          </TabsList>

          <TabsContent value="recommendations" className="space-y-4">
            {criticalRecs.length > 0 && (
              <Alert>
                <AlertTriangle className="h-4 w-4" />
                <AlertTitle>Critical Security Issues Detected</AlertTitle>
                <AlertDescription>
                  You have {criticalRecs.length} critical security issues that require immediate attention.
                </AlertDescription>
              </Alert>
            )}

            <div className="grid gap-4">
              {recommendations.map((rec) => (
                <Card key={rec.id} className={`${rec.priority === 'critical' ? 'border-red-200 bg-red-50/50' : ''}`}>
                  <CardHeader>
                    <div className="flex items-start justify-between">
                      <div className="flex items-start space-x-3">
                        {getCategoryIcon(rec.category)}
                        <div>
                          <CardTitle className="text-lg">{rec.title}</CardTitle>
                          <CardDescription className="mt-1">{rec.description}</CardDescription>
                        </div>
                      </div>
                      <div className="flex flex-col space-y-2">
                        <Badge variant={getPriorityColor(rec.priority) as any}>
                          {rec.priority ? rec.priority.toUpperCase() : 'MEDIUM'}
                        </Badge>
                        {rec.isImplemented && (
                          <Badge variant="outline" className="text-green-600 border-green-600">
                            <CheckCircle2 className="w-3 h-3 mr-1" />
                            Implemented
                          </Badge>
                        )}
                      </div>
                    </div>
                  </CardHeader>
                  <CardContent className="space-y-4">
                    {/* Implementation Details */}
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
                      <div className="flex items-center space-x-2">
                        <Clock className="w-4 h-4 text-gray-500" />
                        <span>Time: {rec.estimatedTimeToImplement}</span>
                      </div>
                      <div className="flex items-center space-x-2">
                        <DollarSign className="w-4 h-4 text-gray-500" />
                        <span>Cost: {rec.estimatedCost}</span>
                      </div>
                      <div className="flex items-center space-x-2">
                        <Users className="w-4 h-4 text-gray-500" />
                        <span>Difficulty: {rec.difficulty}</span>
                      </div>
                    </div>

                    {/* Benefits */}
                    <div>
                      <h4 className="font-semibold text-sm mb-2">Benefits:</h4>
                      <ul className="text-sm text-gray-600 space-y-1">
                        {rec.benefits.map((benefit, index) => (
                          <li key={index} className="flex items-start space-x-2">
                            <CheckCircle2 className="w-3 h-3 text-green-500 mt-0.5 flex-shrink-0" />
                            <span>{benefit}</span>
                          </li>
                        ))}
                      </ul>
                    </div>

                    {/* Action Button */}
                    <div className="pt-4 border-t">
                      {rec.isImplemented ? (
                        <div className="flex items-center text-green-600 text-sm">
                          <CheckCircle2 className="w-4 h-4 mr-2" />
                          Implemented on {rec.implementedAt ? new Date(rec.implementedAt).toLocaleDateString() : 'Unknown date'}
                        </div>
                      ) : (
                        <Button 
                          onClick={() => handleImplementRecommendation(rec.id)}
                          className="w-full sm:w-auto"
                        >
                          Mark as Implemented
                        </Button>
                      )}
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          </TabsContent>

          <TabsContent value="metrics" className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              {metrics?.map((metric) => (
                <Card key={metric.id}>
                  <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                    <CardTitle className="text-sm font-medium capitalize">
                      {metric.metricType ? metric.metricType.replace('_', ' ') : 'Unknown Metric'}
                    </CardTitle>
                    {metric.changePercentage && metric.changePercentage > 0 ? (
                      <TrendingUp className="h-4 w-4 text-green-600" />
                    ) : (
                      <TrendingDown className="h-4 w-4 text-red-600" />
                    )}
                  </CardHeader>
                  <CardContent>
                    <div className="text-2xl font-bold">{metric.value}</div>
                    {metric.changePercentage && (
                      <p className={`text-xs ${metric.changePercentage > 0 ? 'text-green-600' : 'text-red-600'}`}>
                        {metric.changePercentage > 0 ? '+' : ''}{metric.changePercentage}% from previous
                      </p>
                    )}
                  </CardContent>
                </Card>
              ))}
            </div>
          </TabsContent>

          <TabsContent value="assessment" className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle>Assessment Details</CardTitle>
                <CardDescription>
                  Comprehensive security assessment for {assessment?.companyName}
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <h4 className="font-semibold mb-2">Company Information</h4>
                    <dl className="space-y-1 text-sm">
                      <div className="flex justify-between">
                        <dt>Industry:</dt>
                        <dd>{assessment?.industry}</dd>
                      </div>
                      <div className="flex justify-between">
                        <dt>Company Size:</dt>
                        <dd className="capitalize">{assessment?.companySize}</dd>
                      </div>
                      <div className="flex justify-between">
                        <dt>Risk Level:</dt>
                        <dd className={`capitalize font-medium ${getRiskColor(assessment?.riskLevel || '')}`}>
                          {assessment?.riskLevel}
                        </dd>
                      </div>
                    </dl>
                  </div>
                  <div>
                    <h4 className="font-semibold mb-2">Current Security Tools</h4>
                    <div className="flex flex-wrap gap-2">
                      {assessment?.currentSecurityTools?.map((tool, index) => (
                        <Badge key={index} variant="outline">{tool}</Badge>
                      ))}
                    </div>
                    <h4 className="font-semibold mb-2 mt-4">Compliance Requirements</h4>
                    <div className="flex flex-wrap gap-2">
                      {assessment?.complianceRequirements?.map((req, index) => (
                        <Badge key={index} variant="secondary">{req}</Badge>
                      ))}
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}

