client/src/components/chat/ChatHeader.ts.content:
import React, { useState } from 'react';
import { ArrowLeft, MoreVertical, UserX, UserCheck } from 'lucide-react';
import { Avatar, AvatarFallback, AvatarImage } from '../ui/avatar';
import { Button } from '../ui/button';
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from '../ui/dropdown-menu';
import { useLocation } from 'wouter';
import { useBlockUser, useBlockingStatus } from '../../hooks/useChat';

interface ChatHeaderProps {
  otherUser: {
    displayName: string;
    photoURL?: string;
    isOnline?: boolean;
    username?: string;
  };
  otherUserId?: string;
}

export const ChatHeader: React.FC<ChatHeaderProps> = ({ otherUser, otherUserId }) => {
  const [, setLocation] = useLocation();
  const { blockUser, unblockUser } = useBlockUser();
  const { isBlockedByMe, isBlockedByThem, loading: blockingLoading } = useBlockingStatus(otherUserId);
  const [isProcessing, setIsProcessing] = useState(false);

  const handleBlockUser = async () => {
    if (!otherUserId || isProcessing) return;

    setIsProcessing(true);
    try {
      await blockUser(otherUserId);
    } catch (error) {
      console.error('Error blocking user:', error);
    } finally {
      setIsProcessing(false);
    }
  };

  const handleUnblockUser = async () => {
    if (!otherUserId || isProcessing) return;

    setIsProcessing(true);
    try {
      await unblockUser(otherUserId);
    } catch (error) {
      console.error('Error unblocking user:', error);
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <div className="flex items-center justify-between p-4 border-b border-cmo-border bg-white dark:bg-gray-900">
      <div className="flex items-center space-x-3">
        <Button
          variant="ghost"
          size="sm"
          onClick={() => setLocation('/messages')}
          className="lg:hidden"
        >
          <ArrowLeft className="w-4 h-4" />
        </Button>

        <div 
          className="flex items-center space-x-3 cursor-pointer hover:bg-gray-50 dark:hover:bg-gray-800 rounded-lg p-2 -m-2"
          onClick={() => {
            const navigateToUsername = otherUser.username || otherUserId;
            if (navigateToUsername) {
              setLocation(`/u/${navigateToUsername}`);
            }
          }}
        >
          <Avatar className="w-10 h-10">
            <AvatarImage src={otherUser.photoURL} />
            <AvatarFallback>
              {otherUser.displayName.split(' ').map(n => n[0]).join('')}
            </AvatarFallback>
          </Avatar>

          <div>
            <h3 className="font-medium text-cmo-text-primary hover:text-cmo-primary">{otherUser.displayName}</h3>
          {isBlockedByMe && (
            <p className="text-sm text-red-500">Blocked</p>
          )}
          {isBlockedByThem && !isBlockedByMe && (
            <p className="text-sm text-red-500">You are blocked</p>
          )}
          {!isBlockedByMe && !isBlockedByThem && otherUser.isOnline && (
            <p className="text-sm text-green-500">Online</p>
          )}
          {!isBlockedByMe && !isBlockedByThem && !otherUser.isOnline && (
            <p className="text-sm text-cmo-text-secondary">Last seen recently</p>
          )}
          </div>
        </div>
      </div>

      <div className="flex items-center space-x-2">
        <DropdownMenu>
          <DropdownMenuTrigger asChild>
            <Button variant="ghost" size="sm">
              <MoreVertical className="w-4 h-4" />
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent align="end">
            {!blockingLoading && otherUserId && (
              <>
                {isBlockedByMe ? (
                  <DropdownMenuItem 
                    onClick={handleUnblockUser}
                    disabled={isProcessing}
                    className="text-green-600"
                  >
                    <UserCheck className="w-4 h-4 mr-2" />
                    Unblock User
                  </DropdownMenuItem>
                ) : (
                  <DropdownMenuItem 
                    onClick={handleBlockUser}
                    disabled={isProcessing || isBlockedByThem}
                    className="text-red-600"
                  >
                    <UserX className="w-4 h-4 mr-2" />
                    Block User
                  </DropdownMenuItem>
                )}
              </>
            )}
          </DropdownMenuContent>
        </DropdownMenu>
      </div>
    </div>
  );
};

client/src/components/chat/MessageBubble.content:
import React from 'react';
import { format } from 'date-fns';
import { Message } from '../../types/chat';
import { Avatar, AvatarFallback, AvatarImage } from '../ui/avatar';
import { auth } from '../../lib/firebase';

interface MessageBubbleProps {
  message: Message;
  otherUserPhoto?: string;
  otherUserName?: string;
  showAvatar?: boolean;
}

export const MessageBubble: React.FC<MessageBubbleProps> = ({ 
  message, 
  otherUserPhoto, 
  otherUserName,
  showAvatar = true 
}) => {
  const isCurrentUser = message.senderId === auth.currentUser?.uid;

  return (
    <div className={`flex items-end space-x-2 mb-4 ${isCurrentUser ? 'flex-row-reverse space-x-reverse' : ''}`}>
      {showAvatar && !isCurrentUser && (
        <Avatar className="w-8 h-8">
          <AvatarImage src={otherUserPhoto} />
          <AvatarFallback>
            {otherUserName?.split(' ').map(n => n[0]).join('') || 'U'}
          </AvatarFallback>
        </Avatar>
      )}

      <div className={`max-w-xs lg:max-w-md ${isCurrentUser ? 'ml-auto' : ''}`}>
        <div
          className={`px-4 py-2 rounded-lg ${
            isCurrentUser
              ? 'bg-cmo-primary text-white'
              : 'bg-gray-100 dark:bg-gray-800 text-cmo-text-primary dark:text-gray-100'
          }`}
        >
          {message.type === 'text' && message.text && (
            <p className="text-sm whitespace-pre-wrap">{message.text}</p>
          )}

          {message.type === 'image' && message.mediaUrl && (
            <div className="max-w-sm">
              <img
                src={message.mediaUrl}
                alt="Shared image"
                className="rounded-lg w-full h-auto"
                style={{ maxHeight: '300px' }}
              />
            </div>
          )}

          {message.type === 'video' && message.mediaUrl && (
            <div className="max-w-sm">
              <video
                src={message.mediaUrl}
                controls
                className="rounded-lg w-full h-auto"
                style={{ maxHeight: '300px' }}
              />
            </div>
          )}
        </div>

        <p className={`text-xs text-cmo-text-secondary mt-1 ${isCurrentUser ? 'text-right' : 'text-left'}`}>
          {format(message.timestamp, 'HH:mm')}
        </p>
      </div>

      {showAvatar && isCurrentUser && (
        <div className="w-8 h-8" /> // Placeholder for alignment
      )}
    </div>
  );
