export default function UserProfilePage() {
  const [match, params] = useRoute("/u/:username");
  const [activeFilter, setActiveFilter] = useState("All");
  const [expandedComments, setExpandedComments] = useState<{
    [key: string]: boolean;
  }>({});
  const [commentText, setCommentText] = useState<{ [key: string]: string }>({});
  const [profileData, setProfileData] = useState<UserProfile | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [isFollowing, setIsFollowing] = useState<boolean>(false);
  const [followLoading, setFollowLoading] = useState<boolean>(false);
  const [showContactInfo, setShowContactInfo] = useState<boolean>(false);
  const [showFollowersModal, setShowFollowersModal] = useState<boolean>(false);
  const [showFollowingModal, setShowFollowingModal] = useState<boolean>(false);
  const [followers, setFollowers] = useState<UserProfile[]>([]);
  const [following, setFollowing] = useState<UserProfile[]>([]);
  const [followersLoading, setFollowersLoading] = useState<boolean>(false);
  const [followingLoading, setFollowingLoading] = useState<boolean>(false);
  const [showEditProfileModal, setShowEditProfileModal] = useState<boolean>(false);
  const [editFormLoading, setEditFormLoading] = useState<boolean>(false);

  // Suggestions State
  const [suggestions, setSuggestions] = useState<UserProfile[]>([]);
  const [suggestionsLoading, setSuggestionsLoading] = useState<boolean>(false);
  const [followingStatus, setFollowingStatus] = useState<{[key: string]: boolean}>({});
  const [suggestionFollowLoading, setSuggestionFollowLoading] = useState<{[key: string]: boolean}>({});

  // Education and Experience State
  const [education, setEducation] = useState<any[]>([]);
  const [experience, setExperience] = useState<any[]>([]);
  const [educationLoading, setEducationLoading] = useState<boolean>(false);
  const [experienceLoading, setExperienceLoading] = useState<boolean>(false);

  // Skills State
  const [userSkills, setUserSkills] = useState<string[]>([]);
  const [editFormData, setEditFormData] = useState({
    firstName: "",
    lastName: "",
    title: "",
    positionDesignation: "",
    currentCompany: "",
    about: "",
    gender: "",
    dateOfBirth: "",
    city: "",
    state: "",
    country: "",
    userType: "",
    organizationName: "",
  });
  const { user, userProfile } = useAuth();
  const { createConversation } = useCreateConversation();
  const [, setLocation] = useLocation();
  const isMobile = useIsMobile();
  const { toast } = useToast();

  const username = params?.username;
  const filters = ["All", "News", "Posts", "Articles", "Videos", "Jobs"];

  useEffect(() => {
    const fetchUserProfile = async () => {
      if (!username) return;

      setLoading(true);
      setError(null);

      try {
        // Try using username directly with /users/{uid} endpoint
        // In case the backend accepts username as uid parameter
        const userData = await userApiService.getUserByUid(username);
        setProfileData(userData);

        // Update isFollowing from API response
        if ((userData as any).hasOwnProperty('isFollowing')) {
          setIsFollowing((userData as any).isFollowing);
        } else if (userData.followerlist && user?.uid) {
          // Fallback to old logic if isFollowing field not present
          setIsFollowing(userData.followerlist.includes(user.uid));
        }

        // Initialize edit form data
        setEditFormData({
          firstName: userData.firstName || "",
          lastName: userData.lastName || "",
          title: userData.title || "",
          positionDesignation: userData.positionDesignation || "",
          currentCompany: userData.currentCompany || "",
          about: userData.about || "",
          gender: userData.gender || "",
          dateOfBirth: userData.dateOfBirth || "",
          city: userData.city || "",
          state: typeof userData.state === 'string' ? userData.state : userData.state?.name || "",
          country: typeof userData.country === 'string' ? userData.country : userData.country?.name || "",
          userType: userData.userType || "",
          organizationName: userData.organizationName || "",
        });
      } catch (error: any) {
        console.error("Error fetching user profile:", error);
        if (error.message === "USER_NOT_FOUND") {
          setError("User not found");
        } else if (error.message === "AUTH_EXPIRED") {
          setError("Authentication expired");
        } else {
          setError("Failed to load user profile");
        }
      } finally {
        setLoading(false);
      }
    };

    fetchUserProfile();
  }, [username]);

  // Load suggestions
  useEffect(() => {
    const loadSuggestions = async () => {
      setSuggestionsLoading(true);
      try {
        const suggestionsData = await userApiService.getUserSuggestions();
        setSuggestions(suggestionsData || []);
      } catch (error) {
        console.error('Error loading suggestions:', error);
        // Fallback to empty array on error
        setSuggestions([]);
      } finally {
        setSuggestionsLoading(false);
      }
    };

    if (user?.uid) {
      loadSuggestions();
    }
  }, [user?.uid]);

  // Load Education and Experience when profile data is available
  useEffect(() => {
    const loadEducationAndExperience = async () => {
      if (!profileData?.uid) return;

      if ((profileData as any).userType !== "business") {
        // Load Education
        setEducationLoading(true);
        try {
          const educationData = await userApiService.getUserEducation(profileData.uid);
          setEducation(educationData || []);
        } catch (error) {
          console.error('Error loading education:', error);
          setEducation([]);
        } finally {
          setEducationLoading(false);
        }

        // Load Experience
        setExperienceLoading(true);
        try {
          const experienceData = await userApiService.getUserExperience(profileData.uid);
          setExperience(experienceData || []);
        } catch (error) {
          console.error('Error loading experience:', error);
          setExperience([]);
        } finally {
          setExperienceLoading(false);
        }
      }

      // Load Skills for all user types
      try {
        const userSkillsData = await userApiService.getUserSkills(profileData.uid);

        // Handle the new API format
        if (userSkillsData && userSkillsData.skills) {
          // Store the skill names for display
          setUserSkills(userSkillsData.skills.map(skill => skill.name));
        } else {
          setUserSkills([]);
        }
      } catch (error) {
        console.error('Error loading skills:', error);
        setUserSkills([]);
      }
    };

    loadEducationAndExperience();
  }, [profileData?.uid, (profileData as any)?.userType]);

  const handleSendMessage = async () => {
    if (!profileData || !user) return;

    try {
      const conversationId = await createConversation(profileData.uid);
      setLocation(`/chat/${conversationId}`);
    } catch (error: any) {
      console.error("Error creating conversation:", error);

      // Handle Firebase permission errors more gracefully
      if (
        error.message?.includes("permissions") ||
        error.message?.includes("PERMISSION_DENIED")
      ) {
        toast({
          title: "Chat feature not available",
          description:
            "Chat functionality is being set up. Please try again later.",
          variant: "destructive",
        });
      } else {
        toast({
          title: "Failed to start conversation",
          description: "Please try again later.",
          variant: "destructive",
        });
      }
    }
  };

  const handleFollowToggle = async () => {
    if (!profileData || !user || followLoading) return;

    setFollowLoading(true);
    try {
      if (isFollowing) {
        await userApiService.unfollowUser(profileData.uid);
        setIsFollowing(false);
        // Update followerlist locally
        if (profileData.followerlist && user?.uid) {
          profileData.followerlist = profileData.followerlist.filter(uid => uid !== user.uid);
        }
        toast({
          title: "Unfollowed",
          description: `You are no longer following ${profileData.firstName} ${profileData.lastName}`,
        });
      } else {
        await userApiService.followUser(profileData.uid);
        setIsFollowing(true);
        // Update followerlist locally
        if (profileData.followerlist && user?.uid) {
          profileData.followerlist.push(user.uid);
        } else if (user?.uid) {
          profileData.followerlist = [user.uid];
        }
        toast({
          title: "Following",
          description: `You are now following ${profileData.firstName} ${profileData.lastName}`,
        });
      }
    } catch (error: any) {
      console.error("Error toggling follow:", error);
      toast({
        title: "Failed to update follow status",
        description: "Please try again later.",
        variant: "destructive",
      });
    } finally {
      setFollowLoading(false);
    }
  };

  const handleShowFollowers = async () => {
    if (!profileData) return;

    setShowFollowersModal(true);
    setFollowersLoading(true);

    try {
      const followersList = await userApiService.getUserFollowers(profileData.uid);
      setFollowers(followersList || []);
    } catch (error) {
      console.error('Error fetching followers:', error);
      toast({
        title: "Failed to load followers",
        description: "Please try again later.",
        variant: "destructive",
      });
    } finally {
      setFollowersLoading(false);
    }
  };

  const handleShowFollowing = async () => {
    if (!profileData) return;

    setShowFollowingModal(true);
    setFollowingLoading(true);

    try {
      const followingList = await userApiService.getUserFollowing(profileData.uid);
      setFollowing(followingList || []);
    } catch (error) {
      console.error('Error fetching following:', error);
      toast({
        title: "Failed to load following",
        description: "Please try again later.",
        variant: "destructive",
      });
    } finally {
      setFollowingLoading(false);
    }
  };

  const handleEditFormSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!profileData || !user) return;

    setEditFormLoading(true);

    try {
      const updateData: any = {
        ...editFormData,
        company: editFormData.currentCompany,
        description: editFormData.about,
      };

      // Remove empty fields
      Object.keys(updateData).forEach(key => {
        if (updateData[key] === "") {
          delete updateData[key];
        }
      });

      const updatedUser = await userApiService.updateUser(profileData.uid, updateData);
      setProfileData(updatedUser);
      setShowEditProfileModal(false);

      toast({
        title: "Profile updated",
        description: "Your profile has been successfully updated.",
      });
    } catch (error: any) {
      console.error("Error updating profile:", error);
      toast({
        title: "Failed to update profile",
        description: "Please try again later.",
        variant: "destructive",
      });
    } finally {
      setEditFormLoading(false);
    }
  };

  const handleEditFormChange = (field: string, value: string) => {
    setEditFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const toggleComments = (postId: string) => {
    setExpandedComments((prev) => ({
      ...prev,
      [postId]: !prev[postId],
