import React, { useState, useEffect } from 'react';
import { useParams, useLocation } from 'wouter';
import { ArrowLeft, MapPin, Clock, Briefcase, Calendar, Users, Eye, Building, BookOpen } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Separator } from '@/components/ui/separator';
import { useToast } from '@/hooks/use-toast';
import Header from '../components/layout/header';
import { useAuth } from '../hooks/useAuth';

interface JobDetails {
  jobId: string;
  title: string;
  company: string;
  postedBy: string;
  description: string;
  location: string;
  type: 'full-time' | 'part-time' | 'contract' | 'freelance';
  salaryRange: string;
  skills: string[];
  experience: string;
  industry: string;
  postedAt: string;
  deadline: string;
  qualifications: string;
  numberOfVacancies: number;
  isOwner?: boolean;
  alreadyApplied?: boolean;
  applicationId?: string;
  applicationStatus?: 'applied' | 'shortlisted' | 'rejected' | 'hired';
  totalViews?: number;
  totalApplications?: number;
}

const JobDetailsPage = () => {
  const { jobId } = useParams<{ jobId: string }>();
  const [job, setJob] = useState<JobDetails | null>(null);
  const [loading, setLoading] = useState(true);
  const [applying, setApplying] = useState(false);

  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const { user } = useAuth();

  useEffect(() => {
    if (!jobId) {
      setLocation('/jobs');
      return;
    }

    fetchJobDetails();
  }, [jobId]);

  const fetchJobDetails = async () => {
    if (!user) return;

    try {
      setLoading(true);
      const token = await user.getIdToken();
      const apiBaseUrl = import.meta.env.VITE_API_BASE_URL || 'https://cp-backend-service-test-972540571952.asia-south1.run.app';

      const response = await fetch(`${apiBaseUrl}/job?id=${jobId}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        throw new Error(`Failed to fetch job details: ${response.status}`);
      }

      const jobData = await response.json();

      // Transform API response to match our interface
      const transformedJob = {
        jobId: jobData.JobID,
        title: jobData.Title,
        company: jobData.Company,
        location: jobData.Location,
        type: jobData.Type,
        skills: jobData.Skills || [],
        experience: jobData.Experience,
        salaryRange: jobData.SalaryRange,
        description: jobData.Description,
        qualifications: jobData.Qualifications,
        postedAt: jobData.PostedAt,
        deadline: jobData.Deadline,
        numberOfVacancies: jobData.Vacancies || 1,
        industry: jobData.Industry,
        postedBy: jobData.PostedBy,
        alreadyApplied: jobData.AlreadyApplied || false,
        applicationStatus: jobData.ApplicationStatus,
        applicationId: jobData.ApplicationId,
        isOwner: jobData.IsOwner || false
      };

      setJob(transformedJob);
    } catch (error) {
      console.error('Error fetching job details:', error);
      toast({
        title: "Error Loading Job",
        description: "Failed to load job details. Please try again.",
        variant: "destructive",
      });
      // Navigate back to jobs page if job not found
      setLocation('/jobs');
    } finally {
      setLoading(false);
    }
  };

  const handleApplyJob = async () => {
    if (!job || !user) return;

    setApplying(true);
    try {
      const token = await user.getIdToken();
      const apiBaseUrl = import.meta.env.VITE_API_BASE_URL || 'https://cp-backend-service-test-972540571952.asia-south1.run.app';

      const response = await fetch(`${apiBaseUrl}/job/apply`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({ jobId: job.jobId }),
      });

      if (!response.ok) {
        throw new Error(`Failed to apply for job: ${response.status}`);
      }

      toast({
        title: "Application Submitted",
        description: "Your job application has been submitted successfully!",
      });

      // Refresh job details to update application status
      await fetchJobDetails();
    } catch (error) {
      console.error('Error applying for job:', error);
      toast({
        title: "Application Failed",
        description: "Failed to submit application. Please try again.",
        variant: "destructive",
      });
    } finally {
      setApplying(false);
    }
  };

  const formatDate = (dateString?: string) => {
    if (!dateString) return 'Recently posted';

    const date = new Date(dateString);
    if (isNaN(date.getTime())) return 'Recently posted';

    return date.toLocaleDateString('en-IN', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const getJobTypeColor = (type?: string) => {
    if (!type) return 'bg-green-100 text-green-700 border-green-200'; // Default to full-time styling

    switch (type) {
      case 'full-time': return 'bg-green-100 text-green-700 border-green-200';
      case 'part-time': return 'bg-blue-100 text-blue-700 border-blue-200';
      case 'contract': return 'bg-purple-100 text-purple-700 border-purple-200';
      case 'freelance': return 'bg-orange-100 text-orange-700 border-orange-200';
      default: return 'bg-gray-100 text-gray-700 border-gray-200';
    }
  };

  const getApplicationStatusColor = (status: string) => {
    switch (status) {
      case 'applied': return 'bg-blue-100 text-blue-700';
      case 'shortlisted': return 'bg-yellow-100 text-yellow-700';
      case 'rejected': return 'bg-red-100 text-red-700';
      case 'hired': return 'bg-green-100 text-green-700';
      default: return 'bg-gray-100 text-gray-700';
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-cmo-bg-main">
        <Header />
        <div className="flex items-center justify-center min-h-screen">
          <div className="text-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-cmo-primary mx-auto"></div>
            <p className="mt-4 text-cmo-text-secondary">Loading job details...</p>
          </div>
        </div>
      </div>
    );
  }

  if (!job) {
    return (
      <div className="min-h-screen bg-cmo-bg-main">
        <Header />
        <div className="flex items-center justify-center min-h-screen">
          <div className="text-center">
            <p className="text-cmo-text-secondary">Job not found</p>
            <Button onClick={() => setLocation('/jobs')} className="mt-4">
              Back to Jobs
            </Button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-cmo-bg-main">
      <Header />

      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        {/* Header */}
        <div className="mb-6">
          <Button
            variant="ghost"
            onClick={() => setLocation('/jobs')}
            className="mb-4"
            data-testid="button-back-to-jobs"
          >
            <ArrowLeft className="w-4 h-4 mr-2" />
            Back to Jobs
          </Button>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Main Content */}
          <div className="lg:col-span-2 space-y-6">
            {/* Job Header */}
            <Card>
              <CardHeader>
                <div className="flex justify-between items-start">
                  <div>
                    <CardTitle className="text-2xl text-cmo-text-primary mb-2">
                      {job.title}
                    </CardTitle>
                    <div className="flex items-center space-x-2 text-lg text-cmo-text-secondary mb-4">
                      <Building className="w-5 h-5" />
                      <span>{job.company}</span>
                    </div>
                  </div>
                  <Badge variant="outline" className={getJobTypeColor(job.type)}>
                    {job.type ? job.type.replace('-', ' ').toUpperCase() : 'FULL TIME'}
                  </Badge>
                </div>

                <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm text-cmo-text-secondary">
                  <div className="flex items-center">
                    <MapPin className="w-4 h-4 mr-2" />
                    {job.location || 'Location not specified'}
                  </div>
                  <div className="flex items-center">
                    <Clock className="w-4 h-4 mr-2" />
                    {job.experience || 'Not specified'}
                  </div>
                  <div className="flex items-center">
                    <Users className="w-4 h-4 mr-2" />
                    {job.numberOfVacancies || 1} position{(job.numberOfVacancies || 1) > 1 ? 's' : ''}
                  </div>
                  <div className="flex items-center">
                    <Calendar className="w-4 h-4 mr-2" />
                    Posted {formatDate(job.postedAt)}
                  </div>
                </div>
              </CardHeader>
            </Card>

            {/* Job Description */}
            <Card>
              <CardHeader>
                <CardTitle>Job Description</CardTitle>
              </CardHeader>
              <CardContent>
                <p className="text-cmo-text-secondary whitespace-pre-line">
                  {job.description || 'No description available'}
                </p>
              </CardContent>
            </Card>

            {/* Skills & Requirements */}
            <Card>
              <CardHeader>
                <CardTitle>Skills & Requirements</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <h4 className="font-semibold text-cmo-text-primary mb-2">Required Skills</h4>
                  <div className="flex flex-wrap gap-2">
                    {job.skills && Array.isArray(job.skills) && job.skills.map((skill, index) => (
                      <Badge key={index} variant="secondary">
                        {skill}
                      </Badge>
                    ))}
                  </div>
                </div>

                <Separator />

                <div>
                  <h4 className="font-semibold text-cmo-text-primary mb-2">Qualifications</h4>
                  <p className="text-cmo-text-secondary">{job.qualifications}</p>
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Sidebar */}
          <div className="space-y-6">
            {/* Application Status */}
            {job.alreadyApplied && job.applicationStatus && (
              <Card>
                <CardHeader>
                  <CardTitle className="text-lg">Application Status</CardTitle>
                </CardHeader>
                <CardContent>
                  <Badge className={getApplicationStatusColor(job.applicationStatus)}>
                    {job.applicationStatus ? job.applicationStatus.toUpperCase() : 'PENDING'}
                  </Badge>
                  {job.applicationId && (
                    <p className="text-sm text-cmo-text-secondary mt-2">
                      Application ID: {job.applicationId}
                    </p>
                  )}
                </CardContent>
              </Card>
            )}

            {/* Apply Button */}
            {!job.isOwner && (
              <Card>
                <CardContent className="pt-6">
                  {job.alreadyApplied ? (
                    <Button disabled className="w-full">
                      Already Applied
                    </Button>
                  ) : (
                    <Button 
                      onClick={handleApplyJob}
                      disabled={applying}
                      className="w-full"
                      data-testid="button-apply-job"
                    >
                      {applying ? 'Applying...' : 'Apply Now'}
                    </Button>
                  )}
                </CardContent>
              </Card>
            )}

            {/* Job Stats */}
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Job Statistics</CardTitle>
              </CardHeader>
              <CardContent className="space-y-3">
                <div className="flex justify-between items-center">
                  <div className="flex items-center text-sm text-cmo-text-secondary">
                    <Eye className="w-4 h-4 mr-2" />
                    Total Views
                  </div>
                  <span className="font-semibold">{job.totalViews || 0}</span>
                </div>
                <div className="flex justify-between items-center">
                  <div className="flex items-center text-sm text-cmo-text-secondary">
                    <Briefcase className="w-4 h-4 mr-2" />
                    Applications
                  </div>
                  <span className="font-semibold">{job.totalApplications || 0}</span>
                </div>
              </CardContent>
            </Card>

            {/* Job Details */}
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Job Details</CardTitle>
              </CardHeader>
              <CardContent className="space-y-3">
                <div className="flex justify-between items-center">
                  <span className="text-sm text-cmo-text-secondary">Salary Range</span>
                  <span className="font-semibold">{job.salaryRange}</span>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-sm text-cmo-text-secondary">Industry</span>
                  <span className="font-semibold">{job.industry}</span>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-sm text-cmo-text-secondary">Application Deadline</span>
                  <span className="font-semibold">{formatDate(job.deadline)}</span>
                </div>
              </CardContent>
            </Card>
          </div>
        </div>
      </div>
    </div>
  );
};

export default JobDetailsPage;

client/src/pages/post-job.tsx.content:
import React, { useState } from 'react';
import { useLocation } from 'wouter';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Label } from '@/components/ui/label';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { X, Plus, ArrowLeft } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import Header from '../components/layout/header';
import { useAuth } from '../hooks/useAuth';

interface JobFormData {
  title: string;
  company: string;
  description: string;
  location: string;
  type: 'full-time' | 'part-time' | 'contract' | 'freelance';
  salaryRange: string;
  skills: string[];
  experience: string;
  industry: string;
  deadline: string;
  qualifications: string;
  numberOfVacancies: number;
}

const PostJobPage = () => {
  const [formData, setFormData] = useState<JobFormData>({
    title: '',
    company: '',
    description: '',
    location: '',
    type: 'full-time',
    salaryRange: '',
    skills: [],
    experience: '',
    industry: 'Construction',
    deadline: '',
    qualifications: '',
    numberOfVacancies: 1
  });

  const [newSkill, setNewSkill] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [errors, setErrors] = useState<Partial<Record<keyof JobFormData, string>>>({});

  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const { user } = useAuth();

  const handleInputChange = (field: keyof JobFormData, value: string | number) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    // Clear error when user starts typing
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: undefined }));
    }
  };

  const addSkill = () => {
    if (newSkill.trim() && !formData.skills.includes(newSkill.trim())) {
      setFormData(prev => ({
        ...prev,
        skills: [...prev.skills, newSkill.trim()]
      }));
      setNewSkill('');
    }
  };

  const removeSkill = (skillToRemove: string) => {
    setFormData(prev => ({
      ...prev,
      skills: prev.skills.filter(skill => skill !== skillToRemove)
    }));
  };

  const validateForm = (): boolean => {
    const newErrors: Partial<Record<keyof JobFormData, string>> = {};

    if (!formData.title.trim()) newErrors.title = 'Job title is required';
    if (!formData.company.trim()) newErrors.company = 'Company name is required';
    if (!formData.description.trim()) newErrors.description = 'Job description is required';
    if (!formData.location.trim()) newErrors.location = 'Location is required';
    if (!formData.salaryRange.trim()) newErrors.salaryRange = 'Salary range is required';
    if (!formData.experience.trim()) newErrors.experience = 'Experience requirement is required';
    if (!formData.qualifications.trim()) newErrors.qualifications = 'Qualifications are required';
    if (!formData.deadline) newErrors.deadline = 'Application deadline is required';
    if (formData.numberOfVacancies < 1) newErrors.numberOfVacancies = 'Number of vacancies must be at least 1';
    if (formData.skills.length === 0) newErrors.skills = 'At least one skill is required';

    // Validate deadline is in the future
    if (formData.deadline) {
      const deadlineDate = new Date(formData.deadline);
      const today = new Date();
      today.setHours(0, 0, 0, 0);
      if (deadlineDate <= today) {
        newErrors.deadline = 'Deadline must be in the future';
      }
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      toast({
        title: "Validation Error",
        description: "Please fix the errors below and try again.",
        variant: "destructive",
      });
      return;
    }

    if (!user) {
      toast({
        title: "Authentication Required",
        description: "Please log in to post a job.",
        variant: "destructive",
      });
      return;
    }

    setIsSubmitting(true);

    try {
      const token = await user.getIdToken();
      const apiBaseUrl = import.meta.env.VITE_API_BASE_URL || 'https://cp-backend-service-test-972540571952.asia-south1.run.app';

      // Format deadline as ISO string
      const deadlineISO = new Date(formData.deadline).toISOString();

      const response = await fetch(`${apiBaseUrl}/employer/post-job`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({
          ...formData,
          deadline: deadlineISO
        }),
      });

      if (!response.ok) {
        const errorData = await response.text();
        throw new Error(`Failed to post job: ${response.status} ${response.statusText} - ${errorData}`);
      }

      const result = await response.json();

      toast({
        title: "Job Posted Successfully",
        description: "Your job posting has been published and is now visible to candidates.",
      });

      // Navigate back to jobs page
      setLocation('/jobs');

    } catch (error) {
      console.error('Error posting job:', error);
      toast({
        title: "Error Posting Job",
        description: error instanceof Error ? error.message : "An unexpected error occurred. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  // Set minimum date to today
  const today = new Date().toISOString().split('T')[0];

  return (
    <div className="min-h-screen bg-cmo-bg-main">
      <Header />

      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        {/* Header */}
        <div className="mb-6">
          <Button
            variant="ghost"
            onClick={() => setLocation('/jobs')}
            className="mb-4"
            data-testid="button-back-to-jobs"
          >
            <ArrowLeft className="w-4 h-4 mr-2" />
            Back to Jobs
          </Button>

          <h1 className="text-2xl font-bold text-cmo-text-primary">Post a New Job</h1>
          <p className="text-cmo-text-secondary mt-1">
            Fill out the details below to post your job opening
          </p>
        </div>

        <Card>
          <CardHeader>
            <CardTitle>Job Details</CardTitle>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleSubmit} className="space-y-6">
              {/* Basic Information */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="title">Job Title *</Label>
                  <Input
                    id="title"
                    placeholder="e.g. Senior Civil Engineer"
                    value={formData.title}
                    onChange={(e) => handleInputChange('title', e.target.value)}
                    className={errors.title ? "border-red-500" : ""}
                    data-testid="input-job-title"
                  />
                  {errors.title && <p className="text-sm text-red-500">{errors.title}</p>}
                </div>

                <div className="space-y-2">
                  <Label htmlFor="company">Company Name *</Label>
                  <Input
                    id="company"
                    placeholder="e.g. BuildSmart Pvt Ltd"
                    value={formData.company}
                    onChange={(e) => handleInputChange('company', e.target.value)}
                    className={errors.company ? "border-red-500" : ""}
                    data-testid="input-company-name"
                  />
                  {errors.company && <p className="text-sm text-red-500">{errors.company}</p>}
                </div>
              </div>

              {/* Description */}
              <div className="space-y-2">
                <Label htmlFor="description">Job Description *</Label>
                <Textarea
                  id="description"
                  placeholder="Provide a detailed description of the role, responsibilities, and requirements..."
                  value={formData.description}
                  onChange={(e) => handleInputChange('description', e.target.value)}
                  className={`min-h-[120px] ${errors.description ? "border-red-500" : ""}`}
                  data-testid="textarea-job-description"
                />
                {errors.description && <p className="text-sm text-red-500">{errors.description}</p>}
              </div>

              {/* Location and Type */}
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="location">Location *</Label>
                  <Input
                    id="location"
                    placeholder="e.g. Chennai"
                    value={formData.location}
                    onChange={(e) => handleInputChange('location', e.target.value)}
                    className={errors.location ? "border-red-500" : ""}
                    data-testid="input-location"
                  />
                  {errors.location && <p className="text-sm text-red-500">{errors.location}</p>}
                </div>

                <div className="space-y-2">
                  <Label>Job Type *</Label>
                  <Select value={formData.type} onValueChange={(value) => handleInputChange('type', value)}>
                    <SelectTrigger data-testid="select-job-type">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="full-time">Full Time</SelectItem>
                      <SelectItem value="part-time">Part Time</SelectItem>
                      <SelectItem value="contract">Contract</SelectItem>
                      <SelectItem value="freelance">Freelance</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="industry">Industry *</Label>
                  <Select value={formData.industry} onValueChange={(value) => handleInputChange('industry', value)}>
                    <SelectTrigger data-testid="select-industry">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="Construction">Construction</SelectItem>
                      <SelectItem value="Engineering">Engineering</SelectItem>
                      <SelectItem value="Architecture">Architecture</SelectItem>
                      <SelectItem value="Real Estate">Real Estate</SelectItem>
                      <SelectItem value="Infrastructure">Infrastructure</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>

              {/* Salary and Experience */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="salaryRange">Salary Range *</Label>
                  <Input
                    id="salaryRange"
                    placeholder="e.g. ₹8L - ₹12L"
                    value={formData.salaryRange}
                    onChange={(e) => handleInputChange('salaryRange', e.target.value)}
                    className={errors.salaryRange ? "border-red-500" : ""}
                    data-testid="input-salary-range"
                  />
                  {errors.salaryRange && <p className="text-sm text-red-500">{errors.salaryRange}</p>}
                </div>

                <div className="space-y-2">
                  <Label htmlFor="experience">Experience Required *</Label>
                  <Input
                    id="experience"
                    placeholder="e.g. 5+ years"
                    value={formData.experience}
                    onChange={(e) => handleInputChange('experience', e.target.value)}
                    className={errors.experience ? "border-red-500" : ""}
                    data-testid="input-experience"
                  />
                  {errors.experience && <p className="text-sm text-red-500">{errors.experience}</p>}
                </div>
              </div>

              {/* Skills */}
              <div className="space-y-2">
                <Label>Required Skills *</Label>
                <div className="flex gap-2 mb-2">
                  <Input
                    placeholder="Add a skill (e.g. AutoCAD)"
                    value={newSkill}
                    onChange={(e) => setNewSkill(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), addSkill())}
                    data-testid="input-add-skill"
                  />
                  <Button type="button" onClick={addSkill} data-testid="button-add-skill">
                    <Plus className="w-4 h-4" />
                  </Button>
                </div>

                {formData.skills.length > 0 && (
                  <div className="flex flex-wrap gap-2">
                    {formData.skills.map((skill, index) => (
                      <Badge key={index} variant="secondary" className="flex items-center gap-1">
                        {skill}
                        <button
                          type="button"
                          onClick={() => removeSkill(skill)}
                          className="ml-1 hover:bg-gray-200 rounded-full p-0.5"
                          data-testid={`button-remove-skill-${index}`}
                        >
                          <X className="w-3 h-3" />
                        </button>
                      </Badge>
                    ))}
                  </div>
                )}
                {errors.skills && <p className="text-sm text-red-500">{errors.skills}</p>}
              </div>

              {/* Qualifications */}
              <div className="space-y-2">
                <Label htmlFor="qualifications">Qualifications *</Label>
                <Textarea
                  id="qualifications"
                  placeholder="e.g. B.E/B.Tech in Civil Engineering, relevant certifications..."
                  value={formData.qualifications}
                  onChange={(e) => handleInputChange('qualifications', e.target.value)}
                  className={errors.qualifications ? "border-red-500" : ""}
                  data-testid="textarea-qualifications"
                />
                {errors.qualifications && <p className="text-sm text-red-500">{errors.qualifications}</p>}
              </div>

              {/* Deadline and Vacancies */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="deadline">Application Deadline *</Label>
                  <Input
                    id="deadline"
                    type="date"
                    min={today}
                    value={formData.deadline}
                    onChange={(e) => handleInputChange('deadline', e.target.value)}
                    className={errors.deadline ? "border-red-500" : ""}
                    data-testid="input-deadline"
                  />
                  {errors.deadline && <p className="text-sm text-red-500">{errors.deadline}</p>}
                </div>

                <div className="space-y-2">
                  <Label htmlFor="numberOfVacancies">Number of Vacancies *</Label>
                  <Input
                    id="numberOfVacancies"
                    type="number"
                    min="1"
                    value={formData.numberOfVacancies}
                    onChange={(e) => handleInputChange('numberOfVacancies', parseInt(e.target.value) || 1)}
                    className={errors.numberOfVacancies ? "border-red-500" : ""}
                    data-testid="input-vacancies"
                  />
                  {errors.numberOfVacancies && <p className="text-sm text-red-500">{errors.numberOfVacancies}</p>}
                </div>
              </div>

              {/* Submit Buttons */}
              <div className="flex gap-4 pt-4">
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => setLocation('/jobs')}
                  disabled={isSubmitting}
                  data-testid="button-cancel"
                >
                  Cancel
                </Button>
                <Button
                  type="submit"
                  disabled={isSubmitting}
                  data-testid="button-submit-job"
                >
                  {isSubmitting ? 'Posting Job...' : 'Post Job'}
                </Button>
              </div>
            </form>
          </CardContent>
        </Card>
      </div>
    </div>
  );
};

export default PostJobPage;

client/src/components/CommentSection.tsx.content:
import React, { useState } from "react";
import { ThumbsUp, Send } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Avatar, AvatarImage, AvatarFallback } from "@/components/ui/avatar";
import { Card, CardContent } from "@/components/ui/card";
import { useAuth } from "../contexts/AuthContext";

interface Comment {
  id: string;
  userId: string;
  userName: string;
  userTitle?: string;
  userProfilePic?: string;
  content: string;
  likeCount: number;
  createdTime: string;
  isLiked?: boolean;
}

interface CommentSectionProps {
  postId: string;
  commentCount: number;
  isExpanded: boolean;
  onToggleExpand: () => void;
  isDetailPage?: boolean;
}

export default function CommentSection({ 
  postId, 
  commentCount, 
  isExpanded, 
  onToggleExpand,
  isDetailPage = false 
}: CommentSectionProps) {
  const { userProfile } = useAuth();
  const [newComment, setNewComment] = useState("");
  const [comments, setComments] = useState<Comment[]>([]);
  const [isPostingComment, setIsPostingComment] = useState(false);

  // Mock comments data
  const getDummyComments = (): Comment[] => [
    {
      id: "1",
      userId: "user1",
      userName: "Alice Johnson",
      userTitle: "Senior Project Manager",
      userProfilePic: "https://images.unsplash.com/photo-1494790108755-2616b332c88c?w=150&h=150&fit=crop&crop=face",
      content: "This is really exciting! Our team has been looking for solutions like this.",
      likeCount: 5,
      createdTime: new Date(Date.now() - 1 * 60 * 60 * 1000).toISOString()
    },
    {
      id: "2",
      userId: "user2",
      userName: "Mark Thompson",
      userTitle: "Construction Engineer",
      userProfilePic: "https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=150&h=150&fit=crop&crop=face",
      content: "Great innovation! Would love to see this implemented in our upcoming projects.",
      likeCount: 3,
      createdTime: new Date(Date.now() - 2 * 60 * 60 * 1000).toISOString()
    },
    {
      id: "3",
      userId: "user3",
      userName: "Sarah Davis",
      userTitle: "Architecture Lead",
      userProfilePic: "https://images.unsplash.com/photo-1580489944761-15a19d654956?w=150&h=150&fit=crop&crop=face",
      content: "The cost savings potential is remarkable. Looking forward to more updates on this.",
      likeCount: 8,
      createdTime: new Date(Date.now() - 3 * 60 * 60 * 1000).toISOString()
    }
  ];

  React.useEffect(() => {
    if (isExpanded) {
      // Simulate API call
      setTimeout(() => {
        setComments(getDummyComments());
      }, 300);
    }
  }, [isExpanded]);

  const formatTimeAgo = (dateString: string) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffInSeconds = Math.floor((now.getTime() - date.getTime()) / 1000);

    if (diffInSeconds < 60) return "now";
    if (diffInSeconds < 3600) return `${Math.floor(diffInSeconds / 60)}m`;
    if (diffInSeconds < 86400) return `${Math.floor(diffInSeconds / 3600)}h`;
    return `${Math.floor(diffInSeconds / 86400)}d`;
  };

  const handlePostComment = async () => {
    if (!newComment.trim()) return;

    setIsPostingComment(true);

    // Simulate API call
    setTimeout(() => {
      const comment: Comment = {
        id: Date.now().toString(),
        userId: "current-user",
        userName: userProfile?.firstName && userProfile?.lastName 
          ? `${userProfile.firstName} ${userProfile.lastName}`
          : "You",
        userTitle: userProfile?.title || userProfile?.positionDesignation,
        userProfilePic: userProfile?.profilePic || userProfile?.photoUrl,
        content: newComment,
        likeCount: 0,
        createdTime: new Date().toISOString()
      };

      setComments(prev => [comment, ...prev]);
      setNewComment("");
      setIsPostingComment(false);
    }, 500);
  };

  const handleLikeComment = (commentId: string) => {
    setComments(prev => prev.map(comment => {
      if (comment.id === commentId) {
        return {
          ...comment,
          isLiked: !comment.isLiked,
          likeCount: comment.isLiked ? comment.likeCount - 1 : comment.likeCount + 1
        };
      }
      return comment;
    }));
  };

  if (!isExpanded && !isDetailPage) {
    return (
      <Button
        variant="ghost"
        size="sm"
        className="flex-1 hover:bg-gray-50 text-cmo-text-secondary py-2"
        onClick={onToggleExpand}
        data-testid={`button-comment-${postId}`}
      >
        <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-3.582 8-8 8a8.001 8.001 0 01-7.996-7.75c0-4.417 3.582-8 7.996-8s8 3.582 8 7.75z" />
        </svg>
        <span className="text-xs">Comment</span>
      </Button>
    );
  }

  return (
    <div className={`border-t pt-4 ${isDetailPage ? 'h-full' : ''}`}>
      {/* Comment Input */}
      <div className="flex items-center space-x-3 mb-4">
        <Avatar className="w-8 h-8">
          <AvatarImage src={userProfile?.profilePic || userProfile?.photoUrl || ""} />
          <AvatarFallback>
            {userProfile?.firstName?.charAt(0) || "U"}
            {userProfile?.lastName?.charAt(0) || ""}
          </AvatarFallback>
        </Avatar>
        <div className="flex-1 flex space-x-2">
          <Input
            placeholder="Add a comment..."
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
            className="text-sm"
            data-testid={`input-comment-${postId}`}
            onKeyPress={(e) => e.key === 'Enter' && handlePostComment()}
          />
          <Button 
            size="sm" 
            onClick={handlePostComment}
            disabled={!newComment.trim() || isPostingComment}
            data-testid={`button-post-comment-${postId}`}
          >
            <Send className="w-4 h-4" />
          </Button>
        </div>
      </div>

      {/* Comments List */}
      <div className={`space-y-4 ${isDetailPage ? 'overflow-y-auto flex-1' : ''}`}>
        {comments.length === 0 ? (
          <p className="text-sm text-gray-500 text-center py-4">
            No comments yet. Be the first to comment!
          </p>
        ) : (
          comments.map((comment) => (
            <Card key={comment.id} className="bg-gray-50">
              <CardContent className="p-3">
                <div className="flex items-start space-x-3">
                  <Avatar className="w-8 h-8">
                    <AvatarImage src={comment.userProfilePic} />
                    <AvatarFallback className="bg-cmo-primary text-white text-xs">
                      {comment.userName.split(' ').map(n => n[0]).join('')}
                    </AvatarFallback>
                  </Avatar>

                  <div className="flex-1 min-w-0">
                    <div className="flex items-center space-x-2 mb-1">
                      <h4 className="font-medium text-sm text-cmo-text-primary">
                        {comment.userName}
                      </h4>
                      <span className="text-xs text-cmo-text-secondary">
                        {formatTimeAgo(comment.createdTime)}
                      </span>
                    </div>

                    {comment.userTitle && (
                      <p className="text-xs text-cmo-text-secondary mb-2">
                        {comment.userTitle}
                      </p>
                    )}

                    <p className="text-sm text-cmo-text-primary mb-3">
                      {comment.content}
                    </p>

                    <div className="flex items-center space-x-4">
                      <Button
                        variant="ghost"
                        size="sm"
                        className={`text-xs p-1 h-auto ${
                          comment.isLiked ? 'text-blue-600' : 'text-cmo-text-secondary'
                        }`}
                        onClick={() => handleLikeComment(comment.id)}
                        data-testid={`button-like-comment-${comment.id}`}
                      >
                        <ThumbsUp className="w-3 h-3 mr-1" />
                        <span>{comment.likeCount}</span>
                      </Button>
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))
        )}
      </div>

      {!isDetailPage && (
        <div className="mt-4 pt-3 border-t">
          <Button
            variant="ghost"
            size="sm"
            className="w-full text-cmo-text-secondary hover:bg-gray-50"
            onClick={onToggleExpand}
            data-testid={`button-collapse-comments-${postId}`}
          >
            Hide comments
          </Button>
        </div>
      )}
    </div>
  );
}

