import React, { useState } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "./ui/dialog";
import { Button } from "./ui/button";
import { Textarea } from "./ui/textarea";
import { Label } from "./ui/label";
import { Input } from "./ui/input";
import { Upload, FileText, X } from "lucide-react";
import { useToast } from "../hooks/use-toast";
import { useAuth } from "../contexts/AuthContext";

interface JobApplicationModalProps {
  isOpen: boolean;
  onClose: () => void;
  jobId: string;
  jobTitle?: string;
  onApplicationSubmitted?: () => void;
}

export default function JobApplicationModal({
  isOpen,
  onClose,
  jobId,
  jobTitle,
  onApplicationSubmitted,
}: JobApplicationModalProps) {
  const [coverLetter, setCoverLetter] = useState("");
  const [resumeFile, setResumeFile] = useState<File | null>(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const { toast } = useToast();
  const { user } = useAuth();

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      // Check file size (max 10MB)
      if (file.size > 10 * 1024 * 1024) {
        toast({
          title: "File too large",
          description: "Please select a file smaller than 10MB",
          variant: "destructive",
        });
        return;
      }

      // Check file type
      const allowedTypes = [
        "application/pdf",
        "application/msword",
        "application/vnd.openxmlformats-officedocument.wordprocessingml.document",
      ];

      if (!allowedTypes.includes(file.type)) {
        toast({
          title: "Invalid file type",
          description: "Please upload a PDF or Word document",
          variant: "destructive",
        });
        return;
      }

      setResumeFile(file);
    }
  };

  const removeFile = () => {
    setResumeFile(null);
    // Reset the input
    const fileInput = document.getElementById('resume-upload') as HTMLInputElement;
    if (fileInput) {
      fileInput.value = '';
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!coverLetter.trim()) {
      toast({
        title: "Cover letter required",
        description: "Please provide a cover letter for your application",
        variant: "destructive",
      });
      return;
    }

    if (!resumeFile) {
      toast({
        title: "Resume required",
        description: "Please upload your resume",
        variant: "destructive",
      });
      return;
    }

    if (!user) {
      toast({
        title: "Authentication required",
        description: "Please log in to apply for jobs",
        variant: "destructive",
      });
      return;
    }

    try {
      setIsSubmitting(true);

      const token = await user.getIdToken();
      const apiBaseUrl = import.meta.env.VITE_API_BASE_URL || 
        'https://cp-backend-service-test-972540571952.asia-south1.run.app';

      // Create FormData for multipart/form-data request
      const formData = new FormData();
      formData.append('jobId', jobId);
      formData.append('coverLetter', coverLetter);
      formData.append('resume', resumeFile);

      console.log('Submitting job application:', {
        jobId,
        coverLetter: coverLetter.substring(0, 50) + '...',
        resumeFileName: resumeFile.name,
        resumeSize: resumeFile.size
      });

      const response = await fetch(`${apiBaseUrl}/apply-job`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.text();
        throw new Error(`Failed to submit application: ${response.status} ${errorData}`);
      }

      const result = await response.json();
      console.log('Application submitted successfully:', result);

      toast({
        title: "Application Submitted!",
        description: `Your application for ${jobTitle || 'this job'} has been submitted successfully.`,
      });

      // Reset form
      setCoverLetter("");
      setResumeFile(null);

      // Close modal and notify parent
      onClose();
      onApplicationSubmitted?.();

    } catch (error) {
      console.error('Error submitting application:', error);
      toast({
        title: "Application Failed",
        description: error instanceof Error ? error.message : "Failed to submit application. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleClose = () => {
    if (!isSubmitting) {
      setCoverLetter("");
      setResumeFile(null);
      onClose();
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="max-w-md max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="text-lg font-semibold">
            Apply for Job
          </DialogTitle>
          {jobTitle && (
            <p className="text-sm text-gray-600 mt-1">
              {jobTitle}
            </p>
          )}
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          {/* Cover Letter */}
          <div className="space-y-2">
            <Label htmlFor="coverLetter" className="text-sm font-medium">
              Cover Letter <span className="text-red-500">*</span>
            </Label>
            <Textarea
              id="coverLetter"
              placeholder="Tell the employer why you're the perfect fit for this role..."
              value={coverLetter}
              onChange={(e) => setCoverLetter(e.target.value)}
              className="min-h-[100px] resize-y"
              disabled={isSubmitting}
              data-testid="textarea-cover-letter"
            />
            <p className="text-xs text-gray-500">
              {coverLetter.length}/1000 characters
            </p>
          </div>

          {/* Resume Upload */}
          <div className="space-y-2">
            <Label className="text-sm font-medium">
              Resume <span className="text-red-500">*</span>
            </Label>

            {!resumeFile ? (
              <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center hover:border-gray-400 transition-colors">
                <Upload className="w-8 h-8 text-gray-400 mx-auto mb-2" />
                <p className="text-sm text-gray-600 mb-2">
                  Click to upload your resume
                </p>
                <p className="text-xs text-gray-500 mb-3">
                  PDF, DOC, DOCX (Max 10MB)
                </p>
                <Input
                  id="resume-upload"
                  type="file"
                  accept=".pdf,.doc,.docx"
                  onChange={handleFileChange}
                  className="hidden"
                  disabled={isSubmitting}
                />
                <Button
                  type="button"
                  variant="outline"
                  size="sm"
                  onClick={() => document.getElementById('resume-upload')?.click()}
                  disabled={isSubmitting}
                  data-testid="button-upload-resume"
                >
                  Choose File
                </Button>
              </div>
            ) : (
              <div className="flex items-center justify-between p-3 bg-gray-50 border border-gray-200 rounded-lg">
                <div className="flex items-center space-x-3">
                  <FileText className="w-5 h-5 text-blue-600" />
                  <div>
                    <p className="text-sm font-medium text-gray-900">
                      {resumeFile.name}
                    </p>
                    <p className="text-xs text-gray-500">
                      {(resumeFile.size / 1024 / 1024).toFixed(2)} MB
                    </p>
                  </div>
                </div>
                <Button
                  type="button"
                  variant="ghost"
                  size="sm"
                  onClick={removeFile}
                  disabled={isSubmitting}
                  data-testid="button-remove-resume"
                >
                  <X className="w-4 h-4" />
                </Button>
              </div>
            )}
          </div>

          {/* Actions */}
          <div className="flex justify-end space-x-3 pt-4">
            <Button
              type="button"
              variant="outline"
              onClick={handleClose}
              disabled={isSubmitting}
              data-testid="button-cancel-application"
            >
              Cancel
            </Button>
            <Button
              type="submit"
              disabled={isSubmitting || !coverLetter.trim() || !resumeFile}
              data-testid="button-submit-application"
            >
              {isSubmitting ? "Submitting..." : "Submit Application"}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/NewsPostModal.tsx.content:
import React, { useState } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "./ui/dialog";
import { Button } from "./ui/button";
import { Input } from "./ui/input";
import { Textarea } from "./ui/textarea";
import { Avatar, AvatarImage, AvatarFallback } from "./ui/avatar";
import { 
  X, 
  Image as ImageIcon, 
  Smile
} from "lucide-react";
import { useAuth } from "../contexts/AuthContext";
import { useToast } from "../hooks/use-toast";

interface NewsPostModalProps {
  isOpen: boolean;
  onClose: () => void;
  onPostCreated?: () => void;
  onSubmit?: (data: { headline: string; content: string; images: File[] }) => Promise<void>;
  title?: string;
  submitButtonText?: string;
}

export default function NewsPostModal({
  isOpen,
  onClose,
  onPostCreated,
  onSubmit,
  title = "Share News",
  submitButtonText = "Post",
}: NewsPostModalProps) {
  const [headline, setHeadline] = useState("");
  const [content, setContent] = useState("");
  const [images, setImages] = useState<File[]>([]);
  const [isPosting, setIsPosting] = useState(false);
  const { user, userProfile } = useAuth();
  const { toast } = useToast();

  const handleImageUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(event.target.files || []);
    const imageFiles = files.filter(file => file.type.startsWith('image/'));

    if (imageFiles.length + images.length > 5) {
      toast({
        title: "Too many images",
        description: "You can upload up to 5 images per post",
        variant: "destructive",
      });
      return;
    }

    setImages(prev => [...prev, ...imageFiles]);
  };

  const removeImage = (index: number) => {
    setImages(prev => prev.filter((_, i) => i !== index));
  };

  const handlePost = async () => {
    if (!headline.trim() || !content.trim()) {
      toast({
        title: "Missing content",
        description: "Please add both headline and content",
        variant: "destructive",
      });
      return;
    }

    try {
      setIsPosting(true);

      // If onSubmit is provided, use it (for pages like home and articles)
      if (onSubmit) {
        await onSubmit({ headline, content, images });
        resetForm();
        onClose();
        return;
      }

      // Otherwise use the original API call (for news page)
      const token = await user?.getIdToken();
      const apiBaseUrl = import.meta.env.VITE_API_BASE_URL || 
        'https://cp-backend-service-test-972540571952.asia-south1.run.app';

      const formData = new FormData();
      formData.append('headline', headline);
      formData.append('content', content);

      // Add multiple images
      images.forEach((image, index) => {
        formData.append('newsImage', image);
      });

      const response = await fetch(`${apiBaseUrl}/news`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`Failed to create post: ${response.status}`);
      }

      toast({
        title: "News Posted!",
        description: "Your news has been shared successfully",
      });

      // Reset form and close modal
      setHeadline("");
      setContent("");
      setImages([]);
      resetForm();
      onClose();
      if (onPostCreated) onPostCreated();

    } catch (error) {
      console.error('Error creating post:', error);
      toast({
        title: "Post Failed",
        description: "Failed to post news. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsPosting(false);
    }
  };

  const resetForm = () => {
    setHeadline("");
    setContent("");
    setImages([]);
  };

  const handleClose = () => {
    if (!isPosting) {
      setHeadline("");
      setContent("");
      setImages([]);
      onClose();
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="max-w-lg max-h-[90vh] overflow-hidden p-0" aria-describedby="news-post-modal">
        {/* Header */}
        <DialogHeader className="px-4 py-3 border-b">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <Avatar className="w-10 h-10">
                <AvatarImage src={userProfile?.profilePic || userProfile?.photoUrl || user?.photoURL || ""} />
                <AvatarFallback>
                  {userProfile?.firstName?.charAt(0) || user?.displayName?.charAt(0) || user?.email?.charAt(0) || "U"}
                  {userProfile?.lastName?.charAt(0) || ""}
                </AvatarFallback>
              </Avatar>
              <div>
                <DialogTitle className="text-sm font-medium">
                  {userProfile?.firstName && userProfile?.lastName 
                    ? `${userProfile.firstName} ${userProfile.lastName}`
                    : user?.displayName || "User"}
                </DialogTitle>
                <p className="text-xs text-gray-600">Post to Anyone</p>
              </div>
            </div>
            <Button
              variant="ghost"
              size="sm"
              onClick={handleClose}
              disabled={isPosting}
              className="h-6 w-6 p-0"
            >
              <X className="h-4 w-4" />
            </Button>
          </div>
        </DialogHeader>

        {/* Content */}
        <div className="px-4 py-3 flex-1 overflow-y-auto">
          <div className="space-y-3">
            {/* Headline */}
            <Input
              placeholder="Add a headline..."
              value={headline}
              onChange={(e) => setHeadline(e.target.value)}
              className="text-sm font-medium border-none px-0 shadow-none focus-visible:ring-0 placeholder:text-sm"
              disabled={isPosting}
              data-testid="input-modal-headline"
            />

            {/* Content Editor */}
            <Textarea
              placeholder="What do you want to talk about?"
              value={content}
              onChange={(e) => setContent(e.target.value)}
              className="min-h-[120px] resize-none border-none px-0 shadow-none focus-visible:ring-0 text-sm placeholder:text-sm"
              disabled={isPosting}
              data-testid="textarea-modal-content"
            />

            {/* Image Thumbnails */}
            {images.length > 0 && (
              <div className="grid grid-cols-2 gap-3">
                {images.map((image, index) => (
                  <div key={index} className="relative">
                    <img
                      src={URL.createObjectURL(image)}
                      alt={`Upload ${index + 1}`}
                      className="w-full h-32 object-cover rounded-lg"
                    />
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => removeImage(index)}
                      disabled={isPosting}
                      className="absolute top-2 right-2 h-6 w-6 p-0 bg-black/50 hover:bg-black/70 text-white rounded-full"
                      data-testid={`button-remove-image-${index}`}
                    >
                      <X className="h-3 w-3" />
                    </Button>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Footer */}
        <div className="px-4 py-3 border-t">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <Button
                variant="ghost"
                size="sm"
                className="text-gray-600 hover:text-gray-800 p-1.5"
                disabled={isPosting}
                onClick={() => document.getElementById('image-upload')?.click()}
                data-testid="button-add-images"
              >
                <ImageIcon className="w-4 h-4" />
              </Button>
              <input
                id="image-upload"
                type="file"
                accept="image/*"
                multiple
                onChange={handleImageUpload}
                className="hidden"
                disabled={isPosting}
              />
            </div>

            <div className="flex items-center space-x-2">
              <Button
                variant="ghost"
                size="sm"
                className="text-gray-600 hover:text-gray-800 p-1.5"
                disabled={isPosting}
              >
                <Smile className="w-4 h-4" />
              </Button>
              <Button
                onClick={handlePost}
                disabled={!headline.trim() || !content.trim() || isPosting}
                size="sm"
                className="px-3 py-1.5 text-sm"
                data-testid="button-modal-post"
              >
                {isPosting ? "Posting..." : submitButtonText}
              </Button>
            </div>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

